CREATE OR REPLACE PROCEDURE pls_gerar_agravo_benef ( nr_seq_analise_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

/*
ie_opcao_p
G - Gerar agravo no beneficiário da proposta
D - Desfazer agravo no beneficiário da proposta
*/
nr_seq_segurado_w		bigint;
nr_seq_analise_agravo_w		bigint;
nr_seq_agravo_w			bigint;
ie_tipo_agravo_w		varchar(2);
vl_agravo_w			double precision;
qt_parcelas_agravo_w		bigint;
tx_agravo_w			double precision;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_agravo
	where	nr_seq_analise	= nr_seq_analise_p;


BEGIN

select	max(nr_seq_segurado)
into STRICT	nr_seq_segurado_w
from	pls_analise_adesao
where	nr_sequencia	= nr_seq_analise_p;

if (ie_opcao_p = 'G') then
	open C01;
	loop
	fetch C01 into
		nr_seq_analise_agravo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nr_seq_agravo,
			ie_tipo_agravo,
			vl_agravo,
			qt_parcelas_agravo,
			tx_agravo
		into STRICT	nr_seq_agravo_w,
			ie_tipo_agravo_w,
			vl_agravo_w,
			qt_parcelas_agravo_w,
			tx_agravo_w
		from	pls_analise_agravo
		where	nr_sequencia	= nr_seq_analise_agravo_w;

		insert into pls_segurado_agravo(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,
				nm_usuario_nrec,cd_estabelecimento,nr_seq_agravo,ie_tipo_agravo,
				vl_agravo,tx_agravo,qt_parcelas,nr_seq_segurado)
		values (	nextval('pls_segurado_agravo_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),
				nm_usuario_p,cd_estabelecimento_p,nr_seq_agravo_w,ie_tipo_agravo_w,
				vl_agravo_w,tx_agravo_w,qt_parcelas_agravo_w,nr_seq_segurado_w);
		end;
	end loop;
	close C01;
elsif (ie_opcao_p = 'D') then
	delete	FROM pls_segurado_agravo a
	where	a.nr_seq_segurado	= nr_seq_segurado_w
	and	exists (SELECT	1
			from	pls_analise_agravo x
			where	x.nr_seq_agravo = a.nr_seq_agravo
			and	nr_seq_analise	= nr_seq_analise_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
