SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_aihs_pendentes ( nr_seq_competencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_cursor_informacao_w	 varchar(1);



/* Gets all the AIHs that are Identified but don't have a register on the tabs Charged, Granted or Pending */

c_aih CURSOR FOR
	SELECT 	nr_seq_conta,
		vl_deferido,
		vl_ressarcir
	from	pls_processo_contas_comp a
	where	not exists (	SELECT	1
				from 	pls_processo_contas_comp c
				where	c.nr_seq_conta 		= a.nr_seq_conta
				and	c.nr_seq_competencia 	= nr_seq_competencia_p
				and (c.ie_tipo_movimentacao('C', 'D', 'P')))
	and	a.ie_tipo_movimentacao = 'I'
	and	a.nr_seq_competencia = nr_seq_competencia_p;

vet_aih c_aih%rowtype;

BEGIN

ie_cursor_informacao_w := 'N';

open c_aih;
loop
fetch c_aih into
	vet_aih;
EXIT WHEN NOT FOUND; /* apply on c_aih */
	begin
	ie_cursor_informacao_w := 'S';
	insert into pls_processo_contas_comp(
		nr_sequencia,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao,
		dt_atualizacao_nrec,
		cd_estabelecimento,
		vl_ajuste,
		vl_deferido,
		vl_provisao,
		vl_ressarcir,
		ie_tipo_movimentacao,
		cd_conta_cred_ajuste, cd_classif_cred_ajuste,
		cd_conta_deb_ajuste, cd_classif_deb_ajuste,
		nr_seq_esquema_ajuste, cd_historico_ajuste,
		cd_conta_cred_prov, cd_classif_cred_prov,
		cd_conta_deb_prov, cd_classif_deb_prov,
		nr_seq_esquema_prov, cd_historico_prov,
		nr_seq_competencia,
		nr_seq_conta) 
	values (nextval('pls_processo_contas_comp_seq'),
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		cd_estabelecimento_p,
		0,
		vet_aih.vl_deferido,
		0,
		vet_aih.vl_ressarcir,
		'P',
		null, null,
		null, null,
		null, null,
		null, null,
		null, null,
		null, null,
		nr_seq_competencia_p,
		vet_aih.nr_seq_conta);
	end;
end loop;
close c_aih;

if (coalesce(ie_cursor_informacao_w, 'N') = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1099433);
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
