CREATE OR REPLACE PROCEDURE pls_gerar_alerta_evento_macro ( ds_texto_p text, nr_seq_auditoria_p bigint, nm_usuario_p text, ie_forma_envio_w text, ds_texto_alterado_p INOUT text) AS $body$
DECLARE


ds_retorno_w            text;
ds_texto_w              text;
ds_texto_alterado_w     varchar(32764);
pos_macro_w             bigint;
ds_macro_w              varchar(30);
nm_atributo_w           varchar(50);
pos_fim_macro_w         bigint;
vl_atributo_w           numeric(20);
ds_resultado_macro_w    varchar(255);
cd_pessoa_fisica_w      varchar(14);
nr_seq_requisicao_w	bigint;
ds_atributo_w		varchar(4000) := '';
ds_procedimento_w	varchar(255);
ie_forma_envio_SMS	boolean;

C01 CURSOR FOR
	SELECT	Obter_Descricao_Procedimento(cd_procedimento,ie_origem_proced)
	from	pls_auditoria_item
	where	nr_seq_auditoria = nr_seq_auditoria_p;


BEGIN

ds_texto_w      := ds_texto_p;
ds_texto_alterado_w     := ds_texto_w;
ie_forma_envio_SMS :=  ie_forma_envio_w = 'SMS';

select	nr_seq_requisicao
into STRICT	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then

        while((coalesce(ds_texto_w,null) IS NOT NULL AND (coalesce(ds_texto_w,null))::text <> '')) loop
                begin

		select  replace(replace(ds_texto_w,'',' '),chr(13) || chr(10),' ')
		into STRICT    ds_texto_w
		;

		select  position('@' in ds_texto_w)
                into STRICT    pos_macro_w
;

		if (pos_macro_w > 0) then
			select  substr(ds_texto_w,pos_macro_w,length(ds_texto_w)) || ' '
                        into STRICT    ds_texto_w
;

                        select  position(' ' in ds_texto_w)
                        into STRICT    pos_fim_macro_w
;

                        select  Elimina_Caracteres_Especiais(substr(ds_texto_w,1,pos_fim_macro_w -1))
                        into STRICT    ds_macro_w
;

                        if ((coalesce(ds_macro_w,null) IS NOT NULL AND (coalesce(ds_macro_w,null))::text <> '')) then
                                begin
                                select  nm_atributo
                                into STRICT    nm_atributo_w
                                from    pls_macro_alertas_eventos
                                where   upper(nm_macro) = upper(ds_macro_w);
                                exception
                                when others then
                                        nm_atributo_w := null;
                                end;

                                if ((coalesce(nm_atributo_w,null) IS NOT NULL AND (coalesce(nm_atributo_w,null))::text <> '')) then
					if (upper(nm_atributo_w) = 'DS_PROCEDIMENTO') then
						open C01;
						loop
						fetch C01 into
							ds_procedimento_w;
						EXIT WHEN NOT FOUND; /* apply on C01 */
							begin
							if (ie_forma_envio_SMS) then
								ds_procedimento_w := substr(ds_procedimento_w,1,33);
							end if;
							ds_atributo_w := ds_atributo_w || ds_procedimento_w || '/ ';
							end;
						end loop;
						close C01;
						ds_resultado_macro_w := ds_atributo_w;
                                        elsif (upper(nm_atributo_w) = 'NR_SEQ_REQUISICAO') then
						ds_resultado_macro_w := to_char(nr_seq_requisicao_w);
					end if;

                                        select  replace(ds_texto_alterado_w, ds_macro_w, ds_resultado_macro_w)
                                        into STRICT    ds_texto_alterado_w
;
                                end if;
                        end if;

                        select  substr(ds_texto_w,pos_fim_macro_w, length(ds_texto_w))
                        into STRICT    ds_texto_w
;
                else
                        ds_texto_w := '';
                end if;
                end;
        end loop;
	if (ds_texto_alterado_w IS NOT NULL AND ds_texto_alterado_w::text <> '') then
		ds_texto_alterado_p := ds_texto_alterado_w;
	else
		ds_texto_alterado_p := ds_texto_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
