CREATE OR REPLACE PROCEDURE pls_gerar_alt_prod_benef_lote ( nr_seq_lote_p bigint, ie_gerar_cartao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
NR_SEQ_CONTRATO_w		bigint;
dt_solicitacao_w		timestamp;
nr_seq_segurado_w		bigint;
nr_seq_plano_w			bigint;
nr_seq_tabela_w			bigint;
NR_SEQ_MOTIVO_ALT_PLANO_w	bigint;
nr_seq_inclusao_benef_w		bigint;
ie_permite_tab_dif_w		varchar(10);
ie_consiste_tab_contr_w		varchar(10);
			
C01 CURSOR FOR 
	SELECT	NR_SEQ_CONTRATO, 
		coalesce(dt_solicitacao,clock_timestamp()), 
		nr_seq_segurado, 
		nr_seq_plano, 
		NR_SEQ_MOTIVO_ALT_PLANO, 
		nr_sequencia 
	from	pls_inclusao_beneficiario 
	where	nr_seq_lote_inclusao	= nr_seq_lote_p 
	and	ie_status not in ('R','RO') 
	and	coalesce(ie_tipo_operacao,'I')	= 'P';


BEGIN 
 
ie_permite_tab_dif_w	:= coalesce(obter_valor_param_usuario(1202, 9, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
ie_consiste_tab_contr_w	:= coalesce(obter_valor_param_usuario(1202, 10, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
 
open C01;
loop 
fetch C01 into	 
	NR_SEQ_CONTRATO_w, 
	dt_solicitacao_w, 
	nr_seq_segurado_w, 
	nr_seq_plano_w, 
	NR_SEQ_MOTIVO_ALT_PLANO_w, 
	nr_seq_inclusao_benef_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(nr_seq_tabela) 
	into STRICT	nr_seq_tabela_w 
	from	pls_contrato_plano 
	where	nr_seq_contrato	= NR_SEQ_CONTRATO_w 
	and	nr_seq_plano	= nr_seq_plano_w 
	and	ie_situacao	= 'A';
	 
	CALL pls_alterar_produto_benef(	NR_SEQ_CONTRATO_w,nr_seq_segurado_w,nr_seq_plano_w,nr_seq_tabela_w, 
					'B',cd_estabelecimento_p,nm_usuario_p,dt_solicitacao_w, 
					NR_SEQ_MOTIVO_ALT_PLANO_w,ie_permite_tab_dif_w,ie_consiste_tab_contr_w,ie_gerar_cartao_p, 
					'N','S',null,nr_seq_inclusao_benef_w,'N');
					 
	update	pls_inclusao_beneficiario 
	set	dt_efetivacao	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_inclusao_benef_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
