SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_analise_adesao_benef ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
dt_contratacao_w		timestamp;
nr_seq_analise_w		bigint;
ie_exige_declaracao_analise_w	varchar(1);
qt_declaracao_saude_w		bigint;


BEGIN

select	max(ie_exige_declaracao_analise)
into STRICT	ie_exige_declaracao_analise_w
from	pls_parametros
where	cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento;

/* Obter dados dados da pessoa da proposta */

begin
select	cd_pessoa_fisica,
        dt_contratacao
into STRICT	cd_pessoa_fisica_w,
	dt_contratacao_w	
from	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 241396, 'BENEF='||nr_seq_segurado_p || ' - ' || sqlerrm(SQLSTATE) );
end;

if (coalesce(ie_exige_declaracao_analise_w,'N') = 'S') then
	select	count(1)
	into STRICT	qt_declaracao_saude_w
	from	pls_declaracao_segurado
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	ie_status		in ('L','E','P');
	
	if (qt_declaracao_saude_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 241394, null);
	end if;
end if;

select	nextval('pls_analise_adesao_seq')
into STRICT	nr_seq_analise_w
;

insert into pls_analise_adesao(
	nr_sequencia, cd_estabelecimento, nr_seq_segurado,
	cd_pessoa_fisica, dt_analise, dt_atualizacao,
	nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
	ie_status, nm_usuario_analise, dt_solicitacao_inclusao)
values (	nr_seq_analise_w, wheb_usuario_pck.get_cd_estabelecimento, nr_seq_segurado_p,
	cd_pessoa_fisica_w, clock_timestamp(), clock_timestamp(),
	nm_usuario_p, clock_timestamp(), nm_usuario_p,
	'A', nm_usuario_p,dt_contratacao_w);

CALL pls_gerar_ocorrencias_analise(nr_seq_analise_w,wheb_usuario_pck.get_cd_estabelecimento,nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
