SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_analise_macro ( ds_texto_p text, nr_seq_item_p bigint, nm_usuario_p text, ds_texto_alterado_p INOUT text) AS $body$
DECLARE


ds_retorno_w            text;
ds_texto_w              text;
ds_texto_alterado_w     varchar(32764);
pos_macro_w             bigint;
ds_macro_w              varchar(30);
nm_atributo_w           varchar(50);
pos_fim_macro_w         bigint;
vl_atributo_w           numeric(20);
ds_resultado_macro_w    varchar(255);
cd_pessoa_fisica_w      varchar(14);


BEGIN

ds_texto_w      := ds_texto_p;
ds_texto_alterado_w     := ds_texto_w;

if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') then

        WHILE((coalesce(ds_texto_w,null) IS NOT NULL AND (coalesce(ds_texto_w,null))::text <> '')) LOOP
                begin

		select  replace(replace(ds_texto_w,'',' '),chr(13) || chr(10),' ')
		into STRICT    ds_texto_w
		;

		select  position('@' in ds_texto_w)
                into STRICT    pos_macro_w
;

		if (pos_macro_w > 0) then
			select  substr(ds_texto_w,pos_macro_w,length(ds_texto_w)) || ' '
                        into STRICT    ds_texto_w
;

                        select  position(' ' in ds_texto_w)
                        into STRICT    pos_fim_macro_w
;

                        select  Elimina_Caracteres_Especiais(substr(ds_texto_w,1,pos_fim_macro_w -1))
                        into STRICT    ds_macro_w
;

                        if ((coalesce(ds_macro_w,null) IS NOT NULL AND (coalesce(ds_macro_w,null))::text <> '')) then
                                begin
                                select  nm_atributo
                                into STRICT    nm_atributo_w
                                from    pls_macro_analise_aut
                                where   upper(nm_macro) = upper(ds_macro_w);
                                exception
                                when others then
                                        nm_atributo_w := null;
                                end;

                                if ((coalesce(nm_atributo_w,null) IS NOT NULL AND (coalesce(nm_atributo_w,null))::text <> '')) then
					if (upper(nm_atributo_w) = 'NR_SEQ_SEGURADO') then
						select	b.nr_seq_segurado
						into STRICT	vl_atributo_w
						from	pls_auditoria b,
							pls_auditoria_item a
						where	a.nr_Seq_auditoria = b.nr_sequencia
						and	a.nr_sequencia = nr_seq_item_p;
                                        elsif (upper(nm_atributo_w) = 'NR_SEQ_ITEM') then
						vl_atributo_w := nr_seq_item_p;
					end if;

                                        select  pls_substituir_macro_analise(upper(ds_macro_w), nm_atributo_w, vl_atributo_w)
                                        into STRICT    ds_resultado_macro_w
;

                                        select  replace(ds_texto_alterado_w, ds_macro_w, ds_resultado_macro_w)
                                        into STRICT    ds_texto_alterado_w
;
                                end if;
                        end if;

                        select  substr(ds_texto_w,pos_fim_macro_w, length(ds_texto_w))
                        into STRICT    ds_texto_w
;
                else
                        ds_texto_w := '';
                end if;
                end;
        END loop;
	if (ds_texto_alterado_w IS NOT NULL AND ds_texto_alterado_w::text <> '') then
		ds_texto_alterado_p := ds_texto_alterado_w;
	else
		ds_texto_alterado_p := ds_texto_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
