SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_analise_rec_item ( nr_seq_recurso_conta_p pls_rec_glosa_conta.nr_sequencia%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


C01 CURSOR(	nr_seq_recurso_conta_pc		pls_rec_glosa_conta.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia nr_seq_conta_proc,
		null nr_seq_conta_mat,
		b.nr_seq_conta,
		a.nr_sequencia nr_seq_proc_rec,
		null nr_seq_mat_rec
	from	pls_rec_glosa_proc	a,
		pls_conta_proc		b
	where	a.nr_seq_conta_proc	= b.nr_sequencia
	and	a.nr_seq_conta_rec	= nr_seq_recurso_conta_pc
	
union all

	SELECT	null nr_seq_conta_proc,
		b.nr_sequencia nr_seq_conta_mat,
		b.nr_seq_conta,
		null nr_seq_proc_rec,
		a.nr_sequencia nr_seq_mat_rec
	from	pls_rec_glosa_mat	a,
		pls_conta_mat		b
	where	a.nr_seq_conta_mat	= b.nr_sequencia
	and	a.nr_seq_conta_rec	= nr_seq_recurso_conta_pc;

C02 CURSOR(	nr_seq_mat_rec_pc		pls_rec_glosa_mat.nr_sequencia%type,
		nr_seq_proc_rec_pc		pls_rec_glosa_proc.nr_sequencia%type) FOR
	SELECT	substr(tiss_obter_motivo_glosa(a.nr_seq_motivo_glosa,'C'),1,10) cd_codigo,
		a.nr_sequencia nr_seq_glosa_oc
	from	pls_rec_glosa_glosas	a		
	where	(a.nr_seq_motivo_glosa IS NOT NULL AND a.nr_seq_motivo_glosa::text <> '')
	and	a.nr_seq_mat_rec 	= nr_seq_mat_rec_pc
	
union all

	SELECT	substr(tiss_obter_motivo_glosa(a.nr_seq_motivo_glosa,'C'),1,10) cd_codigo,
		a.nr_sequencia nr_seq_glosa_oc
	from	pls_rec_glosa_glosas	a		
	where	(a.nr_seq_motivo_glosa IS NOT NULL AND a.nr_seq_motivo_glosa::text <> '')
	and	a.nr_seq_proc_rec 	= nr_seq_proc_rec_pc;

C03 CURSOR(	nr_seq_recurso_conta_pc		pls_rec_glosa_conta.nr_sequencia%type) FOR
	SELECT	nr_sequencia nr_seq_conta_item
	from	pls_analise_conta_item	
	where	nr_seq_conta_rec	= nr_seq_recurso_conta_pc;
	
C06 CURSOR FOR
	SELECT	substr(tiss_obter_motivo_glosa(a.nr_seq_motivo_glosa,'C'),1,10) cd_codigo,
			a.nr_sequencia nr_seq_glosa_oc,
			(SELECT max(nr_seq_conta) from pls_rec_glosa_conta where nr_sequencia = a.nr_seq_conta_rec) nr_seq_conta
	from	pls_rec_glosa_glosas	a		
	where	(a.nr_seq_motivo_glosa IS NOT NULL AND a.nr_seq_motivo_glosa::text <> '')
	and	a.nr_seq_conta_rec	= nr_seq_recurso_conta_p
	and coalesce(a.nr_seq_proc_rec::text, '') = ''
	and	coalesce(a.nr_seq_mat_rec::text, '') = '';
	
BEGIN
for r_C03_w in C03(nr_seq_recurso_conta_p) loop

	delete	FROM pls_analise_parecer_item
	where	nr_seq_item = r_C03_w.nr_seq_conta_item;
	
	/* Diego OS 292007 
	     Apagar os itens da analise para garatir que não fiquem glosas e ocorrencias de uma análise prévia desta conta.*/
	delete	FROM pls_analise_conta_item
	where	nr_sequencia = r_C03_w.nr_seq_conta_item;
end loop;

/*Inserir glosas e ocorrencias  dos procs e mats*/

for r_C01_w in C01(nr_seq_recurso_conta_p) loop
	
	for r_C02_w in C02(r_C01_w.nr_seq_mat_rec, r_C01_w.nr_seq_proc_rec)loop
		insert into pls_analise_conta_item(cd_codigo, ds_obs_glosa_oc, dt_atualizacao,
			 dt_atualizacao_nrec, ie_situacao, ie_status,
			 ie_tipo, nm_usuario, nm_usuario_nrec,
			 nr_seq_analise, nr_seq_conta, nr_seq_conta_mat,
			 nr_seq_conta_proc, nr_seq_glosa_conta_rec, nr_sequencia,
			 qt_glosa, vl_glosa, ie_fechar_conta, nr_seq_discussao,
			 nr_seq_conta_rec)	
		values (r_C02_w.cd_codigo, null, clock_timestamp(),
			 clock_timestamp(), 'A', 'I',
			 'G', nm_usuario_p, nm_usuario_p,
			 nr_seq_analise_p, r_C01_w.nr_seq_conta, r_C01_w.nr_seq_conta_mat,
			 r_C01_w.nr_seq_conta_proc, r_C02_w.nr_seq_glosa_oc, nextval('pls_analise_conta_item_seq'),
			 null, null, pls_obter_glos_oco_permite(r_C02_w.cd_codigo, 'G'), null,
			 nr_seq_recurso_conta_p);
	end loop;
end loop;

for r_C06_w in C06 loop
		insert into pls_analise_conta_item(cd_codigo, ds_obs_glosa_oc, dt_atualizacao,
			 dt_atualizacao_nrec, ie_situacao, ie_status,
			 ie_tipo, nm_usuario, nm_usuario_nrec,
			 nr_seq_analise, nr_seq_conta, nr_seq_conta_mat,
			 nr_seq_conta_proc, nr_seq_glosa_conta_rec, nr_sequencia,
			 qt_glosa, vl_glosa, ie_fechar_conta, nr_seq_discussao,
			 nr_seq_conta_rec)	
		values (r_C06_w.cd_codigo, null, clock_timestamp(),
			 clock_timestamp(), 'A', 'I',
			 'G', nm_usuario_p, nm_usuario_p,
			 nr_seq_analise_p, r_C06_w.nr_seq_conta, null,
			 null, r_C06_w.nr_seq_glosa_oc, nextval('pls_analise_conta_item_seq'),
			 null, null, pls_obter_glos_oco_permite(r_C06_w.cd_codigo, 'G'), null,
			 nr_seq_recurso_conta_p);
	end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_analise_rec_item ( nr_seq_recurso_conta_p pls_rec_glosa_conta.nr_sequencia%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

