CREATE OR REPLACE PROCEDURE pls_gerar_atendimento_operador ( nr_seq_atendimento_p bigint, nr_seq_operador_p text, qt_tempo_p bigint, nm_usuario_p text, ie_direcionamento_p text) AS $body$
DECLARE


dt_inicio_w			timestamp;
nr_seq_operador_aceita_w	bigint;
qt_tempo_hist_w			bigint;
dt_atualizacao_w		timestamp;
nr_seq_grupo_atend_w		bigint;


BEGIN

select  coalesce(max(dt_fim_atendimento),null)
into STRICT	dt_inicio_w
from    pls_atendimento_operador
where	nr_atendimento = nr_seq_atendimento_p;

if (coalesce(dt_inicio_w::text, '') = '') then
	select	dt_inicio
	into STRICT	dt_inicio_w
	from	pls_atendimento
	where	nr_sequencia = nr_Seq_atendimento_p;
end if;

begin
	select	nr_seq_grupo_atend
	into STRICT	nr_seq_grupo_atend_w
	from	pls_operador
	where	nr_sequencia	= nr_seq_operador_p;
exception
when others then
	nr_seq_grupo_atend_w := null;
end;

insert into pls_atendimento_operador(nr_sequencia, nr_atendimento, nr_seq_operador,
	dt_atualizacao, nm_usuario, dt_fim_atendimento,
	dt_inicio_atendimento, qt_tempo_Atendimento, nr_seq_grupo_atend)
values	(nextval('pls_atendimento_operador_seq'), nr_seq_atendimento_p, nr_seq_operador_p,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	dt_inicio_w, round((clock_timestamp() - dt_inicio_w) * 86400,0), nr_seq_grupo_atend_w);

if (ie_direcionamento_p = 'S') then
	select	max(DT_atualizacao)
	into STRICT	dt_atualizacao_w
	from    pls_atendimento_historico
	where   nr_seq_tipo_historico = 2
	and     nr_seq_atendimento = nr_seq_atendimento_p;

	qt_tempo_hist_w := round((clock_timestamp() - DT_atualizacao_w) * 86400,2);

	select	c.nr_sequencia
	into STRICT	nr_seq_operador_aceita_w
	from	pls_operador	c,
		pessoa_fisica	b,
		usuario		a
	where	a.nm_usuario = nm_usuario_p
	and	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	c.cd_pessoa_fisica = b.cd_pessoa_fisica;

	CALL pls_gerar_historico_atend(qt_tempo_hist_w, nr_seq_atendimento_p,'A',nm_usuario_p,nm_usuario_p,null);

	update	pls_atendimento
	set	nr_seq_operador = nr_seq_operador_aceita_w
	where	nr_sequencia = nr_seq_atendimento_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
