CREATE OR REPLACE PROCEDURE pls_gerar_atend_guia_cat ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
cd_guia_w			varchar(20);
nr_seq_prestador_w		bigint;
dt_atendimento_w		timestamp;


BEGIN



begin
	/* Obter dados da guia */

	select  nr_seq_segurado,
		cd_guia,
		nr_seq_prestador,
		dt_solicitacao
	into STRICT	nr_seq_segurado_w,
		cd_guia_w,
		nr_seq_prestador_w,
		dt_atendimento_w
	from    pls_guia_plano
	where   nr_sequencia = nr_seq_guia_p;
exception
when others then
	cd_guia_w := null;
end;



if (cd_guia_w IS NOT NULL AND cd_guia_w::text <> '') then
	insert into pls_prestador_atend_cat(nr_sequencia, dt_atualizacao, nm_usuario,
	        cd_guia, nr_seq_prestador, dt_atendimento,
		nm_usuario_nrec, dt_atualizacao_nrec, nr_seq_segurado)
	values (	nextval('pls_prestador_atend_cat_seq'), clock_timestamp(), nm_usuario_p,
	        cd_guia_w, nr_seq_prestador_w, dt_atendimento_w,
	        nm_usuario_p, clock_timestamp(), nr_seq_segurado_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
