CREATE OR REPLACE PROCEDURE pls_gerar_auditor_analise_aut (nr_seq_auditoria_p bigint, nr_seq_grupo_analise_p bigint, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE



/*
IE_TIPO_P
I - Iniciar análise
F - Finalizar análise
*/
qt_tempo_auditoria_w	bigint := 0;
nr_seq_analise_aud_w	bigint := 0;
nr_seq_grupo_w			bigint;


BEGIN

if ( ie_tipo_p = 'I' ) then
	select	max(nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	pls_auditoria_grupo
	where	nr_sequencia = nr_seq_grupo_analise_p;

	insert into pls_analise_auditor_aut(nr_sequencia, dt_inicio_analise, nr_seq_auditoria,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, dt_fim_analise, qt_tempo_auditoria,
		nr_seq_grupo, nm_usuario_exec)
	values (nextval('pls_analise_auditor_aut_seq'),clock_timestamp(),nr_seq_auditoria_p,
		clock_timestamp(),nm_usuario_p,clock_timestamp(),
		nm_usuario_p, null, null,
		nr_seq_grupo_w,nm_usuario_p);

elsif ( ie_tipo_p = 'F' ) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_analise_aud_w
	from 	pls_analise_auditor_aut
	where	nr_seq_auditoria 	= nr_seq_auditoria_p
	and		nm_usuario_exec 	= nm_usuario_p;

	if ( nr_seq_analise_aud_w > 0 ) then
		update 	pls_analise_auditor_aut
		set 	dt_fim_analise 		= clock_timestamp(),
				qt_tempo_auditoria 	= round((clock_timestamp() - dt_inicio_analise) * 86400,0),
				dt_atualizacao		= clock_timestamp(),
				nm_usuario			= nm_usuario_p
		where	nr_sequencia		= nr_seq_analise_aud_w;
	end if;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
