CREATE OR REPLACE PROCEDURE pls_gerar_autorizacao_atend ( nr_seq_segurado_p bigint, ie_tipo_guia_p text, cd_medico_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_atendimento_p bigint, nr_seq_evento_atend_p bigint, nr_seq_prestador_p bigint, cd_guia_manual_p text, nr_seq_autorizacao_p INOUT bigint) AS $body$
DECLARE


nr_seq_autorizacao_w		bigint;
nr_seq_plano_w			bigint;
ie_segurado_intercambio_w	varchar(1) := 'N';
cd_guia_w			varchar(20);
ie_guia_manual_w		varchar(1) := 'N';
cd_cooperativa_w		varchar(10);
nr_seq_uni_exec_w		bigint;
nr_seq_prestador_w		bigint := null;
ie_carater_internacao_w		pls_guia_plano.ie_carater_internacao%type;


BEGIN

select	nextval('pls_guia_plano_seq')
into STRICT	nr_seq_autorizacao_w
;

nr_seq_plano_w := pls_obter_produto_benef(nr_seq_segurado_p, clock_timestamp());

cd_guia_w := nr_seq_autorizacao_w;

if (ie_tipo_guia_p <> '3') then
	ie_carater_internacao_w	:= obter_valor_param_usuario(1204, 28, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);

	select	CASE WHEN ie_carater_internacao_w='A' THEN  'E' WHEN ie_carater_internacao_w='M' THEN  'U'  ELSE ie_carater_internacao_w END
	into STRICT	ie_carater_internacao_w
	;
end if;
if (cd_guia_manual_p IS NOT NULL AND cd_guia_manual_p::text <> '') then
	ie_guia_manual_w := obter_valor_param_usuario(1204, 29, Obter_Perfil_Ativo, nm_usuario_p, 0);
	if (ie_guia_manual_w = 'S') then
		cd_guia_w := cd_guia_manual_p;
	end if;
end if;

if (coalesce(nr_seq_prestador_p,0) > 0) then
	nr_seq_prestador_w := nr_seq_prestador_p;
end if;

cd_cooperativa_w := pls_obter_unimed_estab(cd_estabelecimento_p);

if (coalesce(cd_cooperativa_w,'0') <> '0') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_uni_exec_w
	from	pls_congenere
	where	cd_cooperativa = cd_cooperativa_w;
end if;

insert into pls_guia_plano(nr_sequencia, dt_atualizacao, nm_usuario,
	dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_segurado,
	ie_tipo_guia, ie_status, cd_estabelecimento,
	ie_tipo_processo, ie_situacao, cd_medico_solicitante,
	cd_guia, dt_solicitacao, nr_seq_plano,
	ie_estagio,nr_seq_atend_pls, nr_seq_evento_atend,
	nm_usuario_atendimento, ie_origem_solic, nm_usuario_solic,
	cd_guia_manual, nr_seq_prestador, nr_seq_uni_exec,
	ie_carater_internacao)
values (nr_seq_autorizacao_w ,clock_timestamp(),nm_usuario_p,
	clock_timestamp(),nm_usuario_p,nr_seq_segurado_p,
	ie_tipo_guia_p,	'2',cd_estabelecimento_p,
	'M','U',cd_medico_p,
	cd_guia_w, clock_timestamp(), nr_seq_plano_w,
	7,nr_seq_atendimento_p, nr_seq_evento_atend_p,
	nm_usuario_p, 'M', nm_usuario_p,
	cd_guia_manual_p, nr_seq_prestador_w, nr_seq_uni_exec_w,
	ie_carater_internacao_w);

nr_seq_autorizacao_p := nr_seq_autorizacao_w;

ie_segurado_intercambio_w := pls_obter_se_segurado_intercam(nr_seq_autorizacao_w,null,null);

if (ie_segurado_intercambio_w = 'S') then
	CALL pls_alterar_tipo_processo_guia(nr_seq_autorizacao_w, 'I', nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_autorizacao_atend ( nr_seq_segurado_p bigint, ie_tipo_guia_p text, cd_medico_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_atendimento_p bigint, nr_seq_evento_atend_p bigint, nr_seq_prestador_p bigint, cd_guia_manual_p text, nr_seq_autorizacao_p INOUT bigint) FROM PUBLIC;

