SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_beneficiario_web ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
		 
nr_seq_segurado_web_w	pls_segurado_web.nr_sequencia%type;
nr_seq_segurado_w		bigint;	
cd_usuario_plano_w		varchar(255);	
ie_situacao_w			pls_segurado_web.ie_situacao%type;
cd_estabelecimento_w	pls_segurado.cd_estabelecimento%type;
	
	 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
Utilizado no acesso direto do beneficiário quando o beneficiário acessa diretamente pela 
carteirinha no menu Canais. Caso o beneficiário não possua login é criado automaticamente 
 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ x] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
	 
 

BEGIN 
 
if (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') then 
 
	select elimina_caractere_especial(cd_usuario_plano_p) 
	into STRICT  cd_usuario_plano_w 
	;
		 
	select	max(a.nr_sequencia), 
			max(a.ie_situacao) 
	into STRICT	nr_seq_segurado_web_w, 
			ie_situacao_w 
	from	pls_segurado_web a, 
			pls_segurado b, 
			pls_segurado_carteira c 
	where	a.nr_seq_segurado = b.nr_sequencia 
	and		b.nr_sequencia = c.nr_seq_segurado	 
	and		c.cd_usuario_plano = cd_usuario_plano_p 
	and  	pls_obter_dados_segurado(b.nr_sequencia, 'CS') = 'A';
	 
	 
	if ( coalesce(nr_seq_segurado_web_w::text, '') = '' ) then 
	 
		select coalesce(max(b.nr_sequencia),0) 
		into STRICT	nr_seq_segurado_w 
		from	pls_segurado b, 
				pls_segurado_carteira c 
		where	b.nr_sequencia = c.nr_seq_segurado 
		and		c.cd_usuario_plano = cd_usuario_plano_p 
		and  	pls_obter_dados_segurado(b.nr_sequencia, 'CS') = 'A';
		 
		 
		if ((nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') and nr_seq_segurado_w > 0 ) then 
		 
			if (coalesce(cd_estabelecimento_p::text, '') = '') then			 
				select	pls_obter_dados_segurado(nr_seq_segurado_w, 'EST') cd_estabelecimento 
				into STRICT	cd_estabelecimento_w 
				;
			else  
				cd_estabelecimento_w := cd_estabelecimento_p;			
			end if;
 
			insert into pls_segurado_web(nr_sequencia, nr_seq_segurado, ds_senha, 
				ie_situacao, ds_hash, dt_atualizacao, 
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
				ds_observacao, cd_estabelecimento, ie_origem_login) 
			values (nextval('pls_segurado_web_seq'), nr_seq_segurado_w, null, 
				'A', ' ', clock_timestamp(), 
				 nr_seq_segurado_w, clock_timestamp(), nr_seq_segurado_w, 
				 null, cd_estabelecimento_w, 'AR');
		end if;
	elsif (nr_seq_segurado_web_w IS NOT NULL AND nr_seq_segurado_web_w::text <> '' AND (ie_situacao_w = 'I' or ie_situacao_w = 'P')) then 
	 
		update	pls_segurado_web 
		set		ie_situacao = 'A', 
				ds_senha  = NULL, 
				ds_observacao  = NULL, 
				nr_seq_termo  = NULL 
		where 	nr_sequencia = nr_seq_segurado_web_w;		
	 
	end if;
	 
end if;
		 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
