SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_beneficio_web ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
		 
qt_registros_w		bigint;
nr_seq_segurado_w	bigint;	
cd_usuario_plano_w	varchar(255);	
	 

BEGIN 
 
if (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') then 
 
	select elimina_caractere_especial(cd_usuario_plano_p) 
	into STRICT  cd_usuario_plano_w 
	;
 
	select	count(1) 
	into STRICT	qt_registros_w     
	from  	pls_segurado_web 
	where 	pls_obter_dados_segurado(nr_seq_segurado, 'C') = cd_usuario_plano_w 
	and  	ie_situacao = 'A' 
	and  	pls_obter_dados_segurado(nr_seq_segurado, 'DS') = 'Ativo';
 
	if (qt_registros_w = 0) then 
 
		select	max(a.nr_sequencia) nr_seq_segurado 
		into STRICT	nr_seq_segurado_w 
		from	pls_segurado a, 
			pls_segurado_carteira b 
		where	a.nr_sequencia = b.nr_seq_segurado 
		and	cd_usuario_plano_w = b.cd_usuario_plano 
		and	pls_obter_dados_segurado(a.nr_sequencia, 'DS') = 'Ativo';
 
		if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then 
 
			insert into pls_segurado_web(nr_sequencia, nr_seq_segurado, ds_senha, 
				ie_situacao, ds_hash, dt_atualizacao, 
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
				ds_observacao, cd_estabelecimento, ie_origem_login) 
			values (nextval('pls_segurado_web_seq'), nr_seq_segurado_w, null, 
				'A', ' ', clock_timestamp(), 
				 nr_seq_segurado_w, clock_timestamp(), nr_seq_segurado_w, 
				 null, cd_estabelecimento_p, 'AR');
		end if;
		 
	end if;
	 
end if;
		 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
