CREATE OR REPLACE PROCEDURE pls_gerar_boletos_ocorr_financ ( nr_seq_lote_p bigint, cd_banco_p bigint, nr_seq_conta_banco_p bigint, nr_seq_carteira_cobr_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar os boletos para o lote de ocorrência financeira.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	

nr_titulo_w			bigint;
cd_banco_w			bigint;
nr_seq_conta_banco_w		bigint;
nr_seq_carteira_cobr_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_titulo_receber
	from	pls_evento_movimento 	a,
		titulo_receber		b
	where	a.nr_seq_lote 		= nr_seq_lote_p
	and	a.nr_titulo_receber	= b.nr_titulo
	and	b.ie_tipo_titulo <> '10';


BEGIN
if (cd_banco_p IS NOT NULL AND cd_banco_p::text <> '') or (nr_seq_conta_banco_p IS NOT NULL AND nr_seq_conta_banco_p::text <> '') or (nr_seq_carteira_cobr_p IS NOT NULL AND nr_seq_carteira_cobr_p::text <> '') then
	open C01;
	loop
	fetch C01 into	
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	titulo_receber
		set	ie_tipo_titulo		= '1',
			cd_banco		= coalesce(cd_banco_p,cd_banco),
			nr_seq_carteira_cobr	= coalesce(nr_seq_carteira_cobr_p,nr_seq_carteira_cobr),
			nr_seq_conta_banco	= coalesce(nr_seq_conta_banco_p,nr_seq_conta_banco),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_titulo		= nr_titulo_w;
		
		CALL gerar_bloqueto_tit_rec(nr_titulo_w, 'OPSCEOF');
		end;
	end loop;
	close C01;

	update	pls_lote_evento
	set	dt_geracao_boleto 	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia 		= nr_seq_lote_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
