CREATE OR REPLACE PROCEDURE pls_gerar_bonif_indicacao ( nr_seq_segurado_p bigint, nr_seq_bonificacao_p bigint, dt_proposta_p timestamp, cd_estabelecimento_p bigint, nr_seq_benef_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_bonificacao_w	double precision;
tx_bonificacao_w	double precision;
nr_seq_boni_indic_w	bigint;


BEGIN

select	coalesce(vl_bonificacao, 0),
	coalesce(tx_bonificacao, 0)
into STRICT	vl_bonificacao_w,
	tx_bonificacao_w
from	pls_bonificacao_regra	a,
	pls_bonificacao		b
where	a.nr_seq_bonificacao	= b.nr_sequencia
and	a.nr_seq_bonificacao	= nr_seq_bonificacao_p
and	a.ie_tipo_regra		= 'M';

select	nextval('pls_segurado_mensalidade_seq')
into STRICT	nr_seq_boni_indic_w
;

insert into pls_segurado_mensalidade(nr_sequencia, dt_referencia, nr_seq_segurado,
	 ie_tipo_item, vl_item, tx_desconto,
	 ie_situacao, dt_atualizacao, nm_usuario, cd_estabelecimento)
values (nr_seq_boni_indic_w, trunc(dt_proposta_p, 'mm'), nr_seq_segurado_p,
	 '14', vl_bonificacao_w, tx_bonificacao_w,
	 'A', clock_timestamp(), nm_usuario_p, cd_estabelecimento_p);

update	pls_proposta_ind_bonific
set	nr_seq_lancamento	= nr_seq_boni_indic_w
where	nr_seq_segurado		= nr_seq_benef_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
