SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_campo_esp_rec ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, ds_procedimento_p procedimento.ds_procedimento%type, nr_seq_material_p pls_material.nr_sequencia%type, ds_material_p pls_material.ds_material%type, cd_prestador_prot_p pls_prestador.cd_prestador%type, cd_guia_p pls_conta.cd_guia%type, vl_recursado_p pls_conta_proc.vl_glosa%type, dt_atendimento_p pls_conta.dt_atendimento_referencia%type, nr_seq_conta_p INOUT pls_conta.nr_sequencia%type) AS $body$
DECLARE


ds_campo_w		pls_rec_regra_campo_esp.ds_campo%type;
nr_seq_regra_w		pls_rec_regra_campo_esp.nr_sequencia%type;
cd_area_proc_w		area_procedimento.cd_area_procedimento%type;
cd_especialidade_proc_w	especialidade_proc.cd_especialidade%type;
cd_grupo_proc_w		procedimento.cd_grupo_proc%type;
ds_valor_campo_w	procedimento.ds_procedimento%type;
ds_cod_prestador_w	procedimento.ds_procedimento%type;
ds_item_w		procedimento.ds_procedimento%type;
ie_origem_proced_w	procedimento.ie_origem_proced%type;
nr_seq_prest_exec_w	pls_prestador.nr_sequencia%type;
nr_seq_conta_w		pls_conta.nr_sequencia%type;
qt_carac_descricao_w	integer;
qt_prestador_w		integer;
qt_pos_anterior_w	integer;

C01 CURSOR(	cd_area_procedimento_pc		area_procedimento.cd_area_procedimento%type,
		cd_especialidade_pc		especialidade_proc.cd_especialidade%type,
		cd_grupo_proc_pc		procedimento.cd_grupo_proc%type,
		cd_procedimento_pc		procedimento.cd_procedimento%type,
		ie_origem_proced_pc		procedimento.ie_origem_proced%type,
		cd_prestador_prot_pc		pls_prestador.cd_prestador%type,
		nr_seq_material_pc		pls_material.nr_sequencia%type,
		ds_campo_pc			pls_rec_regra_campo_esp.ds_campo%type) FOR
	SELECT	nr_sequencia
	from	pls_rec_regra_campo_esp
	where	ds_campo = ds_campo_pc
	and	clock_timestamp() between dt_inicio_vigencia_ref and dt_fim_vigencia_ref
	and	((coalesce(cd_area_procedimento::text, '') = '') or (cd_area_procedimento = cd_area_procedimento_pc))
	and	((coalesce(cd_especialidade::text, '') = '') or (cd_especialidade = cd_especialidade_pc))
	and	((coalesce(cd_grupo_proc::text, '') = '') or (cd_grupo_proc = cd_grupo_proc_pc))
	and	((coalesce(cd_procedimento::text, '') = '' and coalesce(ie_origem_proced::text, '') = '') or (cd_procedimento = cd_procedimento_pc and ie_origem_proced = ie_origem_proced_pc))
	and	((coalesce(cd_prestador_prot::text, '') = '') or (cd_prestador_prot = cd_prestador_prot_pc))
	and	((coalesce(nr_seq_material::text, '') = '') or (nr_seq_material = nr_seq_material_pc))
	order by
		coalesce(cd_prestador_exec,0),
		coalesce(cd_prestador_prot,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);

C02 CURSOR(nr_seq_regra_pc	pls_rec_regra_campo_esp.nr_sequencia%type) FOR
	SELECT	ds_anterior,
		ds_posterior,
		ie_campo,
		qt_caracteres
	from	pls_rec_regra_esp_compl
	where	nr_seq_regra = nr_seq_regra_pc
	order by
		nr_seq_apresentacao;

C03 CURSOR(cd_prestador_pc		pls_prestador.cd_prestador%type) FOR
	SELECT	a.nr_sequencia nr_seq_prestador
	from	pls_prestador a
	where	a.cd_prestador = cd_prestador_pc
	order by ie_situacao;
BEGIN
if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then

	ds_campo_w := 'DS_PROCEDIMENTO';

	SELECT * FROM pls_obter_estrut_proc(cd_procedimento_p, ie_origem_proced_p, cd_area_proc_w, cd_especialidade_proc_w, cd_grupo_proc_w, ie_origem_proced_w) INTO STRICT cd_area_proc_w, cd_especialidade_proc_w, cd_grupo_proc_w, ie_origem_proced_w;
else
	ds_campo_w := 'DS_MATERIAL';
end if;

open C01(	cd_area_proc_w, cd_especialidade_proc_w, cd_grupo_proc_w,
		cd_procedimento_p, ie_origem_proced_w, cd_prestador_prot_p,
		nr_seq_material_p, ds_campo_w);
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

for r_C02_w in C02(nr_seq_regra_w) loop

	if (ds_campo_w = 'DS_PROCEDIMENTO') then
		ds_item_w := ds_procedimento_p;
	else
		ds_item_w := ds_material_p;
	end if;

	if (coalesce(ds_item_w,'X') <> 'X') then
		qt_carac_descricao_w := length(ds_item_w);
		ds_valor_campo_w := ds_item_w;
		for i in 1..(qt_carac_descricao_w - 1) loop

			if (substr(ds_item_w, i, 1) = r_C02_w.ds_anterior) then

				qt_pos_anterior_w := position(r_C02_w.ds_anterior in ds_item_w);
				ds_valor_campo_w := substr(ds_valor_campo_w, position(r_C02_w.ds_anterior in ds_valor_campo_w) + 1, 255);

				if (r_C02_w.ie_campo = 'CP') then
					if (coalesce(r_C02_w.qt_caracteres,0) > 0) and (r_C02_w.qt_caracteres = length(ds_valor_campo_w)) then
						ds_cod_prestador_w := substr(ds_valor_campo_w, 1, r_C02_w.qt_caracteres);
					end if;

					if (r_C02_w.ds_posterior IS NOT NULL AND r_C02_w.ds_posterior::text <> '') and (coalesce(ds_cod_prestador_w::text, '') = '') and (qt_pos_anterior_w > 0) then
						ds_cod_prestador_w := substr(ds_valor_campo_w, 1, position(r_C02_w.ds_posterior in ds_valor_campo_w || '#') -1);
					end if;
				end if;

				ds_valor_campo_w := substr(ds_valor_campo_w, position(r_C02_w.ds_anterior in ds_valor_campo_w), 255);
			end if;
		end loop;
	end if;
end loop;

if (ds_cod_prestador_w IS NOT NULL AND ds_cod_prestador_w::text <> '') then

	ds_cod_prestador_w := UPPER(ds_cod_prestador_w);

	for r_C03_w in C03(ds_cod_prestador_w) loop

		if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
			select	max(a.nr_sequencia)
			into STRICT	nr_seq_conta_w
			from	pls_conta a
			where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
			and	a.cd_guia_ok = cd_guia_p
			and	exists (	SELECT	1
					from	pls_conta_proc b
					where	a.nr_sequencia = b.nr_seq_conta
					and	b.cd_procedimento = cd_procedimento_p
					and	b.vl_glosa = vl_recursado_p
					and	b.dt_procedimento_referencia_sh = dt_atendimento_p
					and	b.ie_status not in ('D', 'M'));
			if (coalesce(nr_seq_conta_w::text, '') = '') then
				select	max(a.nr_sequencia)
				into STRICT	nr_seq_conta_w
				from	pls_conta a
				where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
				and	a.cd_guia_pesquisa = cd_guia_p
				and	exists (	SELECT	1
						from	pls_conta_proc b
						where	a.nr_sequencia = b.nr_seq_conta
						and	b.cd_procedimento = cd_procedimento_p
						and	b.vl_glosa = vl_recursado_p
						and	b.dt_procedimento_referencia_sh = dt_atendimento_p
						and	b.ie_status not in ('D', 'M'));

				if (coalesce(nr_seq_conta_w::text, '') = '') then
					select	max(a.nr_sequencia)
					into STRICT	nr_seq_conta_w
					from	pls_conta a
					where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
					and	a.cd_guia_ok = cd_guia_p
					and	exists (	SELECT	1
							from	pls_conta_proc b
							where	a.nr_sequencia = b.nr_seq_conta
							and	b.cd_procedimento = cd_procedimento_p
							and	b.vl_glosa = vl_recursado_p
							and	b.ie_status not in ('D', 'M'));

					if (coalesce(nr_seq_conta_w::text, '') = '') then
						select	max(a.nr_sequencia)
						into STRICT	nr_seq_conta_w
						from	pls_conta a
						where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
						and	a.cd_guia_pesquisa = cd_guia_p
						and	exists (	SELECT	1
								from	pls_conta_proc b
								where	a.nr_sequencia = b.nr_seq_conta
								and	b.cd_procedimento = cd_procedimento_p
								and	b.vl_glosa = vl_recursado_p
								and	b.ie_status not in ('D', 'M'));

						if (coalesce(nr_seq_conta_w::text, '') = '') then
							select	max(a.nr_sequencia)
							into STRICT	nr_seq_conta_w
							from	pls_conta a
							where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
							and	a.cd_guia_ok = cd_guia_p
							and	exists (	SELECT	1
									from	pls_conta_proc b
									where	a.nr_sequencia = b.nr_seq_conta
									and	b.cd_procedimento = cd_procedimento_p
									and	b.vl_glosa > 0
									and	b.ie_status not in ('D', 'M'));

							if (coalesce(nr_seq_conta_w::text, '') = '') then
								select	max(a.nr_sequencia)
								into STRICT	nr_seq_conta_w
								from	pls_conta a
								where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
								and	a.cd_guia_pesquisa = cd_guia_p
								and	exists (	SELECT	1
										from	pls_conta_proc b
										where	a.nr_sequencia = b.nr_seq_conta
										and	b.cd_procedimento = cd_procedimento_p
										and	b.vl_glosa > 0
										and	b.ie_status not in ('D', 'M'));
							end if;
						end if;
					end if;
				end if;
			end if;
		else

			select	max(a.nr_sequencia)
			into STRICT	nr_seq_conta_w
			from	pls_conta a
			where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
			and	a.cd_guia_ok = cd_guia_p
			and	exists (	SELECT	1
					from	pls_conta_mat b
					where	a.nr_sequencia = b.nr_seq_conta
					and	b.nr_seq_material = nr_seq_material_p
					and	b.vl_glosa = vl_recursado_p
					and	b.dt_atendimento_referencia = dt_atendimento_p
					and	b.ie_status not in ('D', 'M'));
			if (coalesce(nr_seq_conta_w::text, '') = '') then
				select	max(a.nr_sequencia)
				into STRICT	nr_seq_conta_w
				from	pls_conta a
				where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
				and	a.cd_guia_ok = cd_guia_p
				and	exists (	SELECT	1
						from	pls_conta_mat b
						where	a.nr_sequencia = b.nr_seq_conta
						and	b.nr_seq_material = nr_seq_material_p
						and	b.vl_glosa = vl_recursado_p
						and	b.dt_atendimento_referencia = dt_atendimento_p
						and	b.ie_status not in ('D', 'M'));

				if (coalesce(nr_seq_conta_w::text, '') = '') then
					select	max(a.nr_sequencia)
					into STRICT	nr_seq_conta_w
					from	pls_conta a
					where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
					and	a.cd_guia_ok = cd_guia_p
					and	exists (	SELECT	1
							from	pls_conta_mat b
							where	a.nr_sequencia = b.nr_seq_conta
							and	b.nr_seq_material = nr_seq_material_p
							and	b.vl_glosa = vl_recursado_p
							and	b.ie_status not in ('D', 'M'));
					if (coalesce(nr_seq_conta_w::text, '') = '') then
						select	max(a.nr_sequencia)
						into STRICT	nr_seq_conta_w
						from	pls_conta a
						where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
						and	a.cd_guia_pesquisa = cd_guia_p
						and	exists (	SELECT	1
								from	pls_conta_mat b
								where	a.nr_sequencia = b.nr_seq_conta
								and	b.nr_seq_material = nr_seq_material_p
								and	b.vl_glosa = vl_recursado_p
								and	b.ie_status not in ('D', 'M'));

						if (coalesce(nr_seq_conta_w::text, '') = '') then
							select	max(a.nr_sequencia)
							into STRICT	nr_seq_conta_w
							from	pls_conta a
							where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
							and	a.cd_guia_ok = cd_guia_p
							and	exists (	SELECT	1
									from	pls_conta_mat b
									where	a.nr_sequencia = b.nr_seq_conta
									and	b.nr_seq_material = nr_seq_material_p
									and	b.vl_glosa > 0
									and	b.ie_status not in ('D', 'M'));
							if (coalesce(nr_seq_conta_w::text, '') = '') then
								select	max(a.nr_sequencia)
								into STRICT	nr_seq_conta_w
								from	pls_conta a
								where	a.nr_seq_prestador_exec  = r_C03_w.nr_seq_prestador
								and	a.cd_guia_pesquisa = cd_guia_p
								and	exists (	SELECT	1
										from	pls_conta_mat b
										where	a.nr_sequencia = b.nr_seq_conta
										and	b.nr_seq_material = nr_seq_material_p
										and	b.vl_glosa > 0
										and	b.ie_status not in ('D', 'M'));
							end if;
						end if;
					end if;
				end if;
			end if;
		end if;

		if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then
			exit;
		end if;
	end loop;

	nr_seq_conta_p := nr_seq_conta_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_campo_esp_rec ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type, ds_procedimento_p procedimento.ds_procedimento%type, nr_seq_material_p pls_material.nr_sequencia%type, ds_material_p pls_material.ds_material%type, cd_prestador_prot_p pls_prestador.cd_prestador%type, cd_guia_p pls_conta.cd_guia%type, vl_recursado_p pls_conta_proc.vl_glosa%type, dt_atendimento_p pls_conta.dt_atendimento_referencia%type, nr_seq_conta_p INOUT pls_conta.nr_sequencia%type) FROM PUBLIC;

