CREATE OR REPLACE PROCEDURE pls_gerar_cancel_interc_reg ( nr_seq_requisicao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Gerar o cancelamento da requisição de intercâmbio, conforme regra de cancelamento automático 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/				 
				 
nr_seq_erro_w			bigint;
nr_seq_motivo_cancel_w		bigint;
nr_seq_regra_w			bigint;
qt_inconsist_w			bigint;


BEGIN 
if (pls_obter_se_controle_estab('RE') = 'S') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_regra_w 
	from	pls_regra_cancel_aut_req 
	where	ie_aplicacao_regra	= 3 
	and	ie_situacao		= 'A' 
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento);
else 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_regra_w 
	from	pls_regra_cancel_aut_req 
	where	ie_aplicacao_regra	= 3 
	and	ie_situacao		= 'A';
end if;
begin 
	select	nr_seq_motivo_cancel 
	into STRICT	nr_seq_motivo_cancel_w 
	from	pls_regra_cancel_aut_req 
	where	nr_sequencia	= nr_seq_regra_w;
exception 
when others then 
	nr_seq_motivo_cancel_w	:= 0;
end;
 
if (coalesce(nr_seq_motivo_cancel_w,0)	<> 0) then 
	CALL pls_cancelar_requisicao(nr_seq_requisicao_p,nr_seq_motivo_cancel_w,'Regra de cancelamento automático nº '||nr_seq_regra_w, 
				nm_usuario_p,cd_estabelecimento_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
