CREATE OR REPLACE PROCEDURE pls_gerar_cid_guia_conta ( nr_seq_guia_p bigint, nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_diagnostico_w		varchar(2000);
ie_tipo_doenca_w		varchar(1);
ie_indicacao_acidente_w		varchar(1);
cd_doenca_w			varchar(10);
ie_classificacao_w		varchar(1);


BEGIN
if (coalesce(nr_seq_guia_p,0) > 0) then
	insert into pls_diagnostico_conta(ie_tipo_doenca, ie_indicacao_acidente, cd_doenca,
		ds_diagnostico, ie_classificacao, nr_sequencia,
		nm_usuario, nm_usuario_nrec, dt_atualizacao,
		dt_atualizacao_nrec, nr_seq_conta)
		(SELECT	ie_tipo_doenca, ie_indicacao_acidente, cd_doenca,
			ds_diagnostico,	ie_classificacao, nextval('pls_diagnostico_conta_seq'),
			nm_usuario_p, nm_usuario_p, clock_timestamp(),
			clock_timestamp(), nr_seq_conta_p
		from	pls_diagnostico
		where	nr_seq_guia = nr_seq_guia_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
