SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_ci_solic_portal_web ( ie_tipo_cominic_p bigint, nr_seq_lote_inc_p bigint, nr_seq_inclusao_p bigint, nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

/*
IE_TIPO_COMINIC_P
1               Solicitação alteração dados usuário prestador web
2               Solicitação de inclusão de beneficiário web
3               Solicitação de rescisão de contrato web
4               Solicitação de alteração de cadastro beneficiário web
5               Solicitação 2º via carteira web
*/
cd_perfil_w		integer;
ds_titulo_w		varchar(255);
ds_comunicado_w		varchar(4000);
ds_comunicado_ww	varchar(4000);
nm_usuario_destino_w	varchar(4000);
ds_segurado_w		varchar(255);
nm_pessoa_fisica_w	varchar(255);

C01 CURSOR FOR
	SELECT	cd_perfil,
		substr(ds_titulo,1,255),
		substr(ds_comunicado,1,3800),
		substr(nm_usuario_destino,1,4000)
	from	pls_atend_comunic_interna
	where	ie_tipo_comunicado	= ie_tipo_cominic_p
	and	ie_situacao		= 'A'
	and (coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento and pls_obter_se_controle_estab('RE') = 'S')
	
union all

	SELECT	cd_perfil,
		substr(ds_titulo,1,255),
		substr(ds_comunicado,1,3800),
		substr(nm_usuario_destino,1,4000)
	from	pls_atend_comunic_interna
	where	ie_tipo_comunicado	= ie_tipo_cominic_p
	and	ie_situacao		= 'A'
	and 	pls_obter_se_controle_estab('RE') = 'N';
	

BEGIN

open C01;
loop
fetch C01 into	
	cd_perfil_w,
	ds_titulo_w,
	ds_comunicado_w,
	nm_usuario_destino_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_tipo_cominic_p	= 2) then
		if (nr_seq_inclusao_p IS NOT NULL AND nr_seq_inclusao_p::text <> '') then
			ds_comunicado_ww	:= substr(ds_comunicado_w||'\par'||' Obs: Inclusão individual nº '||nr_seq_inclusao_p||'\par'||' Usuário: '||nm_usuario_p,1,4000);
		elsif (nr_seq_lote_inc_p IS NOT NULL AND nr_seq_lote_inc_p::text <> '') then
			ds_comunicado_ww	:= substr(ds_comunicado_w||'\par'||' Obs: Inclusão do lote nº '||nr_seq_lote_inc_p||'\par'||' Usuário: '||nm_usuario_p,1,4000);
		end if;
	elsif (ie_tipo_cominic_p	= 3) then
		ds_segurado_w		:= substr(pls_obter_dados_segurado(nr_seq_segurado_p,'C')||' - '||pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255);
		ds_comunicado_ww	:= substr(ds_comunicado_w||'\par'||' Obs: Solicitada a rescisão do beneficiário '||ds_segurado_w||'\par'||' Usuário: '||nm_usuario_p,1,4000);
	elsif (ie_tipo_cominic_p	= 4) then
		nm_pessoa_fisica_w	:= substr(cd_pessoa_fisica_p||' - '||obter_nome_pf(cd_pessoa_fisica_p),1,255);
		ds_comunicado_ww	:= substr(ds_comunicado_w||'\par'||' Obs: Solicitada alteração no cadastro da pessoa fisica: '||nm_pessoa_fisica_w||'\par'||' Usuário: '||obter_nome_pf(cd_pessoa_fisica_p),1,4000);
	elsif (ie_tipo_cominic_p	= 5) then
		ds_segurado_w		:= substr(pls_obter_dados_segurado(nr_seq_segurado_p,'C')||' - '||pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255);
		ds_comunicado_ww	:= substr(ds_comunicado_w||'\par'||' Obs: Solicitada 2ª via de carteira para o beneficiário '||ds_segurado_w||'\par'||' Usuário: '||substr(pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255),1,4000);
	end if;
	
	CALL Gerar_Comunic_Padrao(	clock_timestamp(), ds_titulo_w, coalesce(ds_comunicado_ww,ds_comunicado_w), nm_usuario_p,
				'N', nm_usuario_destino_w, 'N',
				null, cd_perfil_w, null,
				null, clock_timestamp(), null, null);
	
	if (coalesce(ie_commit_p, 'S') = 'S') then
		commit;
	end if;

	ds_comunicado_ww	:= '';
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_ci_solic_portal_web ( ie_tipo_cominic_p bigint, nr_seq_lote_inc_p bigint, nr_seq_inclusao_p bigint, nr_seq_segurado_p bigint, cd_pessoa_fisica_p text, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

