SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_classif_mens ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_classificacao_credito_w	varchar(255);
cd_classificacao_debito_w	varchar(255);
ie_classif_preco_w		varchar(10);
ie_classif_preco_deb_w		varchar(10);
ie_classif_regulamentacao_w	varchar(10);
ie_classif_contratacao_w	varchar(10);
ie_classif_segmentacao_w	varchar(10);
ie_classif_conta_w		varchar(10);
ie_classif_subconta_w		varchar(10);
cd_classif_grupo_w		varchar(10);
cd_classif_subgrupo_w		varchar(10);
cd_classif_conta_w		varchar(10);
ie_classif_plano_contas_w	varchar(10);
ie_classif_tp_contrato_w	varchar(10);
ie_classif_contraprestacoes_w	varchar(10);

ie_preco_w			varchar(2);
ie_tipo_segurado_w		varchar(2);
ie_regulamentacao_w		varchar(2);
ie_tipo_contratacao_w		varchar(2);
ie_segmentacao_w		varchar(2);
nr_seq_item_w			bigint;
ie_cancelamento_w		varchar(1);
cd_conta_credito_w		varchar(20);
cd_conta_debito_w		varchar(20);
ie_esquema_contabil_w		varchar(2);

ie_tipo_item_w			varchar(2);
nr_seq_plano_w			bigint;
nr_seq_sca_w			bigint;
nr_seq_tipo_lanc_w		bigint;
dt_referencia_w			timestamp;
nr_seq_regra_esquema_w		bigint;
cd_historico_padrao_w		bigint;
cd_historico_estorno_w		bigint;
cd_historico_baixa_w		bigint;
ds_erro_w			varchar(4000);
nr_seq_contrato_w		bigint;

cd_conta_rec_w			varchar(20);
cd_conta_deb_w			varchar(20);
nr_seq_conta_w			bigint;
nr_seq_conta_copartic_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_grupo_ans_w		bigint;
nr_seq_grupo_ans_ww		bigint;
nr_seq_grupo_superior_w		bigint;
ie_classif_grupo_w		varchar(5);
ie_classif_grupo_ww		varchar(5);
nr_seq_regra_copartic_w		bigint;
ie_tipo_relacao_w		varchar(2);
ie_tipo_ato_w			varchar(255)	:= '1';
ie_classif_relacao_w		varchar(5);
ie_classif_ato_w		varchar(5);

ie_tipo_grupo_w			varchar(1);
nr_seq_tipo_atendimento_w	bigint;
cd_medico_executor_w		varchar(10);
ie_regime_internacao_w		varchar(1);
nr_seq_conselho_w		bigint;
ie_tipo_guia_w			varchar(2);
ie_tipo_beneficiario_w		varchar(3);
ie_ato_cooperado_w		varchar(5);
ie_classif_ato_coop_w		varchar(2);

cd_classif_ato_coop_w		varchar(30);
cd_classif_ato_aux_w		varchar(30);
cd_classif_ato_nao_coop_w	varchar(30);
cd_conta_ato_coop_w		varchar(20);
cd_conta_ato_aux_w		varchar(20);
cd_conta_ato_nao_coop_w		varchar(20);

vl_ato_cooperado_princ_w	double precision;
vl_ato_cooperado_aux_w		double precision;
vl_ato_nao_cooperado_w		double precision;
vl_item_w			double precision;
tx_valor_ato_w			double precision;
ie_tipo_ato_cooperado_w		varchar(2);
vl_diferenca_w			double precision;

nr_seq_regra_esquema_antec_w	bigint;
cd_conta_credito_antec_w	varchar(20);
cd_conta_debito_antec_w		varchar(20);
cd_historico_padrao_antec_w	bigint;
cd_historico_estorno_antec_w	bigint;
cd_historico_baixa_antec_w	bigint;
ie_esquema_contabil_antec_w	varchar(2);
cd_classificacao_cred_antec_w	varchar(30);
cd_classificacao_deb_antec_w	varchar(30);

C01 CURSOR FOR
	SELECT	'NA',
		e.nr_sequencia,
		a.ie_tipo_contratacao,
		a.ie_preco,
		a.ie_segmentacao,
		a.ie_regulamentacao,
		b.ie_tipo_segurado,
		c.ie_cancelamento,
		e.ie_tipo_item,
		e.nr_seq_plano,
		f.nr_seq_plano,
		e.nr_seq_tipo_lanc,
		c.dt_referencia,
		b.nr_seq_contrato,
		e.nr_seq_conta,
		g.ie_tipo_beneficiario
	FROM pls_lote_mensalidade h, pls_contrato g, pls_mensalidade_segurado d, pls_mensalidade c, pls_segurado b, pls_plano a, pls_mensalidade_seg_item e
LEFT OUTER JOIN pls_sca_vinculo f ON (e.nr_seq_vinculo_sca = f.nr_sequencia)
WHERE c.nr_seq_lote		= h.nr_sequencia and h.nr_sequencia		= nr_seq_lote_p and d.nr_seq_mensalidade	= c.nr_sequencia and b.nr_sequencia		= d.nr_seq_segurado and a.nr_sequencia		= b.nr_seq_plano and e.nr_seq_mensalidade_seg = d.nr_sequencia  and b.nr_seq_contrato	= g.nr_sequencia and not exists (	SELECT	1
				from	pls_contab_ato_item x
				where	x.ie_tipo_item_mensalidade = e.ie_tipo_item)
	
UNION ALL

	select	'AC',
		e.nr_sequencia,
		a.ie_tipo_contratacao,
		a.ie_preco,
		a.ie_segmentacao,
		a.ie_regulamentacao,
		b.ie_tipo_segurado,
		c.ie_cancelamento,
		e.ie_tipo_item,
		e.nr_seq_plano,
		f.nr_seq_plano,
		e.nr_seq_tipo_lanc,
		c.dt_referencia,
		b.nr_seq_contrato,
		e.nr_seq_conta,
		g.ie_tipo_beneficiario
	FROM pls_lote_mensalidade h, pls_contrato g, pls_mensalidade_segurado d, pls_mensalidade c, pls_segurado b, pls_plano a, pls_mensalidade_seg_item e
LEFT OUTER JOIN pls_sca_vinculo f ON (e.nr_seq_vinculo_sca = f.nr_sequencia)
WHERE c.nr_seq_lote		= h.nr_sequencia and h.nr_sequencia		= nr_seq_lote_p and d.nr_seq_mensalidade	= c.nr_sequencia and b.nr_sequencia		= d.nr_seq_segurado and a.nr_sequencia		= b.nr_seq_plano and e.nr_seq_mensalidade_seg = d.nr_sequencia  and b.nr_seq_contrato	= g.nr_sequencia and exists (	select	1
				from	pls_contab_ato_item x
				where	x.ie_tipo_item_mensalidade = e.ie_tipo_item);

C02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_conta_credito,
		cd_conta_debito,
		cd_historico_padrao,
		cd_historico_estorno,
		cd_historico_baixa,
		ie_esquema_contabil
	from	pls_esquema_contabil
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_tipo_regra		= 'M'
	and	dt_referencia_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_w)
	and (coalesce(ie_tipo_item_mensalidade,ie_tipo_item_w) 		= ie_tipo_item_w)
	and (coalesce(nr_seq_tipo_lancamento,coalesce(nr_seq_tipo_lanc_w,0))	= coalesce(nr_seq_tipo_lanc_w,0))
	and (coalesce(nr_seq_plano,coalesce(nr_seq_plano_w,0))		= coalesce(nr_seq_plano_w,0))
	and (coalesce(nr_seq_sca,coalesce(nr_seq_sca_w,0))			= coalesce(nr_seq_sca_w,0))
	order by	coalesce(nr_seq_plano,0),
			coalesce(nr_seq_sca,0),
			coalesce(ie_tipo_item_mensalidade,'A'),
			coalesce(nr_seq_tipo_lancamento,0),
			coalesce(cd_conta_credito,'A'),
			coalesce(cd_conta_debito,'A'),
			coalesce(ie_esquema_contabil,0),
			coalesce(dt_inicio_vigencia,clock_timestamp());

C03 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.cd_procedimento,
		b.ie_origem_proced,
		a.nr_seq_regra,
		a.cd_conta_cred,
		a.cd_conta_deb,
		b.ie_ato_cooperado
	from	pls_conta_coparticipacao a,
		pls_conta_proc b
	where	a.nr_seq_conta_proc = b.nr_sequencia
	and	exists (	SELECT	1
				from	pls_conta_proc x
				where	x.nr_sequencia = a.nr_seq_conta_proc
				and	x.nr_seq_conta = nr_seq_conta_w)
	
UNION

	select	a.nr_sequencia,
		0 cd_procedimento,
		0 ie_origem_proced,
		a.nr_seq_regra,
		a.cd_conta_cred,
		a.cd_conta_deb,
		null
	from	pls_conta_coparticipacao a
	where	a.nr_seq_conta = nr_seq_conta_w
	and	not exists (	select	1
				from	pls_conta_proc x
				where	x.nr_sequencia = a.nr_seq_conta_proc
				and	x.nr_seq_conta = nr_seq_conta_w);

C04 CURSOR FOR
	SELECT	a.tx_valor_ato,
		a.ie_tipo_ato_cooperado
	from	pls_contab_ato_regra	a,
		pls_contab_ato_cooperado b,
		pls_contab_ato_item	c
	where	a.nr_seq_ato_cooperado	= b.nr_sequencia
	and	c.nr_seq_ato_cooperado	= b.nr_sequencia
	and	c.ie_tipo_item_mensalidade = ie_tipo_item_w;

C05 CURSOR FOR
	SELECT	nr_sequencia,
		cd_conta_credito,
		cd_conta_debito,
		cd_historico_padrao,
		cd_historico_estorno,
		cd_historico_baixa,
		ie_esquema_contabil
	from	pls_esquema_contabil
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_tipo_regra		= 'AM'
	and	dt_referencia_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_w)
	and (coalesce(ie_tipo_item_mensalidade,ie_tipo_item_w) 		= ie_tipo_item_w)
	and (coalesce(nr_seq_tipo_lancamento,coalesce(nr_seq_tipo_lanc_w,0))	= coalesce(nr_seq_tipo_lanc_w,0))
	and (coalesce(nr_seq_plano,coalesce(nr_seq_plano_w,0))		= coalesce(nr_seq_plano_w,0))
	and (coalesce(nr_seq_sca,coalesce(nr_seq_sca_w,0))			= coalesce(nr_seq_sca_w,0))
	order by	coalesce(nr_seq_plano,0),
			coalesce(nr_seq_sca,0),
			coalesce(ie_tipo_item_mensalidade,'A'),
			coalesce(nr_seq_tipo_lancamento,0),
			coalesce(cd_conta_credito,'A'),
			coalesce(cd_conta_debito,'A'),
			coalesce(ie_esquema_contabil,0),
			coalesce(dt_inicio_vigencia,clock_timestamp());


BEGIN

open C01;
loop
fetch C01 into
	ie_ato_cooperado_w,
	nr_seq_item_w,
	ie_tipo_contratacao_w,
	ie_preco_w,
	ie_segmentacao_w,
	ie_regulamentacao_w,
	ie_tipo_segurado_w,
	ie_cancelamento_w,
	ie_tipo_item_w,
	nr_seq_plano_w,
	nr_seq_sca_w,
	nr_seq_tipo_lanc_w,
	dt_referencia_w,
	nr_seq_contrato_w,
	nr_seq_conta_w,
	ie_tipo_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_regra_esquema_w,
		cd_conta_credito_w,
		cd_conta_debito_w,
		cd_historico_padrao_w,
		cd_historico_estorno_w,
		cd_historico_baixa_w,
		ie_esquema_contabil_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;

	open C05;
	loop
	fetch C05 into
		nr_seq_regra_esquema_antec_w,
		cd_conta_credito_antec_w,
		cd_conta_debito_antec_w,
		cd_historico_padrao_antec_w,
		cd_historico_estorno_antec_w,
		cd_historico_baixa_antec_w,
		ie_esquema_contabil_antec_w;
	EXIT WHEN NOT FOUND; /* apply on C05 */
	end loop;
	close C05;

	if (coalesce(ie_esquema_contabil_w::text, '') = '') then
		begin
		select	cd_classificacao
		into STRICT	cd_classificacao_credito_w
		from	conta_contabil
		where	cd_conta_contabil = cd_conta_credito_w;

		select	cd_classificacao
		into STRICT	cd_classificacao_debito_w
		from	conta_contabil
		where	cd_conta_contabil = cd_conta_debito_w;

		update	pls_mensalidade_seg_item
		set	cd_conta_rec		= cd_conta_credito_w,
			cd_conta_deb		= cd_conta_debito_w,
			cd_historico		= cd_historico_padrao_w,
			cd_historico_baixa	= cd_historico_baixa_w,
			cd_classif_cred		= cd_classificacao_credito_w,
			cd_classif_deb		= cd_classificacao_debito_w
		where	nr_sequencia		= nr_seq_item_w;
		exception
		when others then
			ds_erro_w	:= '';
		end;
	elsif (ie_esquema_contabil_w = '1') then /* ANS - IN 36 */
		if (ie_tipo_item_w <> '3') then /* Se não for coparticipação */
			/*				CONTA CRÉDITO				*/

			cd_classificacao_credito_w	:= '3.'; /* Valor fixo referente à classe*/
			/* 2 - Grupo */

			if (ie_tipo_item_w in ('11','2')) then
				cd_classif_grupo_w	:= '3.';
			else
				cd_classif_grupo_w	:= '1.';
			end if;

			/* 3 - Sub-grupo */

			cd_classif_subgrupo_w	:= '1.';

			/* 4 - Conta */

			if (ie_cancelamento_w = 'E') then /* Extorno deve ser "(-)Contraprest.CANCELADAS  Médico Hospitalar (estorno 1)" */
				ie_classif_conta_w	:= '5.';
			elsif (ie_tipo_beneficiario_w	= 'ENB') then
				ie_classif_conta_w	:= '3.';
			elsif (ie_tipo_segurado_w in ('A','B')) then /* Beneficiário do plano e SCA deve ser "Contraprestação emitidas Prest. Médico Hosp" */
				ie_classif_conta_w	:= '1.';
			elsif (ie_tipo_segurado_w in ('C','I','H','P','R','T')) then /* Outros beneficiário como intercâmbio... deve ser "Contraprest. Responsabilidade assumida Médico Hospitalar" */
				ie_classif_conta_w	:= '3.';
			else /* Se entrar no else é porque ocorreu algum problema */
				ie_classif_conta_w	:= '0.';
			end if;

			/* 5 - Subconta - Valor Fixo - "Contraprestações emitidas" */

			ie_classif_subconta_w	:= '1.';

			/*6 - Formação do preço */

			if	((ie_tipo_segurado_w = 'A') or (ie_tipo_item_w in ('11','2'))) then /* Beneficiário de SCA não tem preço de produto */
				ie_classif_preco_w	:= '9.';
			elsif (ie_preco_w = '1') then
				ie_classif_preco_w	:= '1.';
			elsif (ie_preco_w in ('2','3')) then
				ie_classif_preco_w	:= '2.';
			end if;
			/* classificação preço débito */

			if (ie_preco_w = '1') then
				ie_classif_preco_deb_w	:= '1.';
			elsif (ie_preco_w in ('2','3')) then
				ie_classif_preco_deb_w	:= '2.';
			end if;

			/* 7 - Tipo item */

			if (ie_tipo_item_w = '11') then
				ie_classif_contraprestacoes_w	:= '4.';
			elsif (ie_tipo_item_w = '2') then /* inscrição */
				ie_classif_contraprestacoes_w	:= '2.';
			else
				ie_classif_contraprestacoes_w	:= '1.';
			end if;

			/* 8 .. 9 - Contin. Plano Contas. Valores fixos: "Contraprestações Pecuniárias", "MODALIDADES DE PRE-PAGAMENTO", "CONTRAPRESTAÇÃO PECUNIÁRIA" */

			ie_classif_plano_contas_w	:= '0.';

			/* 10 - Regulamentação */

			if (ie_regulamentacao_w	= 'R') then
				ie_classif_regulamentacao_w	:= '1.';
			else
				ie_classif_regulamentacao_w	:= '2.';
			end if;

			/* 11 - Tipo de contratação */

			if (ie_tipo_contratacao_w = 'I') then
				ie_classif_contratacao_w	:= '1.';
			elsif (ie_tipo_contratacao_w = 'CE') then
				ie_classif_contratacao_w	:= '3.';
			elsif (ie_tipo_contratacao_w = 'CA') then
				ie_classif_contratacao_w	:= '4.';
			end if;

			/* 12 e 13 - Segmentação */

			ie_classif_segmentacao_w	:= lpad(ie_segmentacao_w,2,'0');

			/*				1 				2			3			4			5			6		*/

			cd_classificacao_credito_w := 	cd_classificacao_credito_w ||	cd_classif_grupo_w ||	cd_classif_subgrupo_w ||ie_classif_conta_w || ie_classif_subconta_w ||	ie_classif_preco_w ||
			/*				7				8				9 */

							ie_classif_contraprestacoes_w || ie_classif_plano_contas_w || '0';

			if (cd_classif_grupo_w = '1.') then /*	 10					11				12 e 13	 */
				cd_classificacao_credito_w	:= 	cd_classificacao_credito_w ||'.'|| 	ie_classif_regulamentacao_w ||	ie_classif_contratacao_w || 	ie_classif_segmentacao_w;
			end if;

			/* 9 - Ato cooperado */

			if (ie_ato_cooperado_w = 'AC') then
				cd_classif_ato_coop_w	:=	'3.' ||	cd_classif_grupo_w ||	cd_classif_subgrupo_w ||ie_classif_conta_w || ie_classif_subconta_w ||	ie_classif_preco_w ||
									ie_classif_contraprestacoes_w || ie_classif_plano_contas_w || 	'1';
				cd_classif_ato_aux_w	:=	'3.' ||	cd_classif_grupo_w ||	cd_classif_subgrupo_w ||ie_classif_conta_w || ie_classif_subconta_w ||	ie_classif_preco_w ||
									ie_classif_contraprestacoes_w || ie_classif_plano_contas_w || 	'2';
				cd_classif_ato_nao_coop_w	:=	'3.' ||	cd_classif_grupo_w ||	cd_classif_subgrupo_w ||ie_classif_conta_w || ie_classif_subconta_w ||	ie_classif_preco_w ||
									ie_classif_contraprestacoes_w || ie_classif_plano_contas_w || 	'7';

				if (cd_classif_grupo_w = '1.') then
					cd_classif_ato_coop_w		:= 	cd_classif_ato_coop_w ||'.'|| 	ie_classif_regulamentacao_w ||	ie_classif_contratacao_w || 	ie_classif_segmentacao_w;
					cd_classif_ato_aux_w		:= 	cd_classif_ato_aux_w ||'.'|| 	ie_classif_regulamentacao_w ||	ie_classif_contratacao_w || 	ie_classif_segmentacao_w;
					cd_classif_ato_nao_coop_w	:= 	cd_classif_ato_nao_coop_w ||'.'|| 	ie_classif_regulamentacao_w ||	ie_classif_contratacao_w || 	ie_classif_segmentacao_w;
				end if;

				select	max(cd_conta_contabil)
				into STRICT	cd_conta_ato_coop_w
				from	conta_contabil
				where	cd_classificacao	= cd_classif_ato_coop_w
				and	ie_tipo			= 'A';

				select	max(cd_conta_contabil)
				into STRICT	cd_conta_ato_aux_w
				from	conta_contabil
				where	cd_classificacao	= cd_classif_ato_aux_w
				and	ie_tipo			= 'A';

				select	max(cd_conta_contabil)
				into STRICT	cd_conta_ato_nao_coop_w
				from	conta_contabil
				where	cd_classificacao	= cd_classif_ato_nao_coop_w
				and	ie_tipo			= 'A';

				select	vl_item
				into STRICT	vl_item_w
				from	pls_mensalidade_seg_item
				where	nr_sequencia	= nr_seq_item_w;

				open C04;
				loop
				fetch C04 into
					tx_valor_ato_w,
					ie_tipo_ato_cooperado_w;
				EXIT WHEN NOT FOUND; /* apply on C04 */
					begin
					if (ie_tipo_ato_cooperado_w = '1') then
						vl_ato_cooperado_princ_w	:= ((vl_item_w / 100) * tx_valor_ato_w);
					elsif (ie_tipo_ato_cooperado_w = '2') then
						vl_ato_cooperado_aux_w		:= ((vl_item_w / 100) * tx_valor_ato_w);
					elsif (ie_tipo_ato_cooperado_w = '3') then
						vl_ato_nao_cooperado_w		:= ((vl_item_w / 100) * tx_valor_ato_w);
					end if;
					end;
				end loop;
				close C04;

				if	(vl_item_w <> (vl_ato_cooperado_princ_w + vl_ato_cooperado_aux_w + vl_ato_nao_cooperado_w)) then
					vl_diferenca_w	:= vl_item_w - (vl_ato_cooperado_princ_w + vl_ato_cooperado_aux_w + vl_ato_nao_cooperado_w);

					vl_ato_nao_cooperado_w	:= vl_ato_nao_cooperado_w + vl_diferenca_w;
				end if;
			else
				cd_conta_ato_coop_w	:= null;
				cd_conta_ato_aux_w	:= null;
				cd_conta_ato_nao_coop_w	:= null;
			end if;

			/*				CONTA DÉBITO				*/

			if (pls_obter_dados_contrato(nr_seq_contrato_w,'TE') = 'PJ') then
				ie_classif_tp_contrato_w	:= '1.';
				cd_classificacao_cred_antec_w	:= '1.2.3.1.1.1.7.1.0';
				cd_classificacao_deb_antec_w	:= '1.2.3.1.1.1.7.1.0';
			else
				ie_classif_tp_contrato_w	:= '3.';
				cd_classificacao_cred_antec_w	:= '1.2.3.1.1.1.7.3.0';
				cd_classificacao_deb_antec_w	:= '1.2.3.1.1.1.7.3.0';
			end if;

			if (ie_tipo_item_w = '2') then /* inscrição */
				cd_classificacao_credito_w	:= '3.3.1.1.1.9.2.0.0.0.0.01';
				cd_classificacao_debito_w	:= '1.2.3.1.1.'||ie_classif_preco_deb_w||ie_classif_tp_contrato_w||'0.0.0.0.01';
			else
				cd_classificacao_debito_w	:= '1.2.3.';

				if (ie_tipo_beneficiario_w	= 'ENB') then
					cd_classificacao_debito_w	:= cd_classificacao_debito_w || '4.1.';
				else
					cd_classificacao_debito_w	:= cd_classificacao_debito_w || '1.1.';
				end if;

				/*				1 ... 5				6				7				8 e 9	*/

				cd_classificacao_debito_w := 	cd_classificacao_debito_w ||	ie_classif_preco_deb_w ||	ie_classif_tp_contrato_w ||	'0.0';

			end if;

			select	max(cd_conta_contabil)
			into STRICT	cd_conta_credito_w
			from	conta_contabil
			where	cd_classificacao	= cd_classificacao_credito_w
			and	ie_tipo			= 'A';

			select	max(cd_conta_contabil)
			into STRICT	cd_conta_debito_w
			from	conta_contabil
			where	cd_classificacao	= cd_classificacao_debito_w
			and	ie_tipo			= 'A';

			select	max(cd_conta_contabil)
			into STRICT	cd_conta_credito_antec_w
			from	conta_contabil
			where	cd_classificacao	= cd_classificacao_cred_antec_w
			and	ie_tipo			= 'A';

			select	max(cd_conta_contabil)
			into STRICT	cd_conta_debito_antec_w
			from	conta_contabil
			where	cd_classificacao	= cd_classificacao_deb_antec_w
			and	ie_tipo			= 'A';

			begin
			update	pls_mensalidade_seg_item
			set	cd_conta_rec		= cd_conta_credito_w,
				cd_conta_deb		= cd_conta_debito_w,
				cd_historico		= cd_historico_padrao_w,
				cd_historico_baixa	= cd_historico_baixa_w,
				cd_conta_ato_cooperado	= cd_conta_ato_coop_w,
				cd_conta_ato_auxiliar	= cd_conta_ato_aux_w,
				cd_conta_ato_nao_coop	= cd_conta_ato_nao_coop_w,
				vl_ato_cooperado	= vl_ato_cooperado_princ_w,
				vl_ato_auxiliar		= vl_ato_cooperado_aux_w,
				vl_ato_nao_cooperado	= vl_ato_nao_cooperado_w,
				cd_historico_antec	= cd_historico_padrao_antec_w,
				cd_historico_antec_baixa = cd_historico_baixa_antec_w,
				cd_conta_deb_antecip	= cd_conta_credito_antec_w,
				cd_conta_rec_antecip	= cd_conta_debito_antec_w,
				cd_classif_cred		= cd_classificacao_credito_w,
				cd_classif_deb		= cd_classificacao_debito_w,
				cd_classif_cred_ant	= cd_classificacao_cred_antec_w,
				cd_classif_deb_ant	= cd_classificacao_deb_antec_w,
				cd_classif_cooperado	= cd_classif_ato_coop_w,
				cd_classif_ato_auxiliar	= cd_classif_ato_aux_w,
				cd_classif_nao_cooperado = cd_classif_ato_nao_coop_w
			where	nr_sequencia		= nr_seq_item_w;
			exception
			when others then
				ds_erro_w	:= '';
			end;
		elsif (ie_tipo_item_w = '3') then
			open C03;
			loop
			fetch C03 into
				nr_seq_conta_copartic_w,
				cd_procedimento_w,
				ie_origem_proced_w,
				nr_seq_regra_copartic_w,
				cd_conta_rec_w,
				cd_conta_deb_w,
				ie_ato_cooperado_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin
				/*			CONTA CRÉDITO				*/

				cd_classificacao_credito_w	:= '4.1.2.';

				if (ie_tipo_beneficiario_w = 'ENB') then
					cd_classif_conta_w	:= '7.';
				else
					cd_classif_conta_w	:= '5.';
				end if;

				/* 5 - Grupo ANS */

				select	ie_tipo_guia,
					nr_seq_tipo_atendimento,
					cd_medico_executor,
					ie_regime_internacao
				into STRICT	ie_tipo_guia_w,
					nr_seq_tipo_atendimento_w,
					cd_medico_executor_w,
					ie_regime_internacao_w
				from	pls_conta
				where	nr_sequencia = nr_seq_conta_w;

				select	max(nr_seq_conselho)
				into STRICT	nr_seq_conselho_w
				from	pessoa_fisica
				where	cd_pessoa_fisica	= cd_medico_executor_w;

				select	pls_obter_grupo_ans(cd_procedimento_w, ie_origem_proced_w, nr_seq_conselho_w,
					nr_seq_tipo_atendimento_w, ie_tipo_guia_w, ie_regime_internacao_w,
					null, 'G', coalesce(cd_estabelecimento_p,0),
					nr_seq_conta_w)
				into STRICT	nr_seq_grupo_ans_w
				;

				select	max(ie_tipo_grupo_ans)
				into STRICT	nr_seq_grupo_ans_w
				from	ans_grupo_despesa
				where	nr_sequencia	= nr_seq_grupo_ans_w;

				if (coalesce(nr_seq_grupo_ans_w,0) > 0) then
					select	max(nr_seq_grupo_superior)
					into STRICT	nr_seq_grupo_superior_w
					from	ans_grupo_despesa
					where	nr_sequencia	= nr_seq_grupo_ans_w;

					select	max(ie_tipo_grupo_ans)
					into STRICT	nr_seq_grupo_superior_w
					from	ans_grupo_despesa
					where	nr_sequencia	= nr_seq_grupo_superior_w;
				end if;

				if (coalesce(nr_seq_grupo_superior_w,0) = 0) then
					nr_seq_grupo_ans_ww	:= nr_seq_grupo_ans_w;
				else
					nr_seq_grupo_ans_ww	:= nr_seq_grupo_superior_w;
				end if;

				if (nr_seq_grupo_ans_ww = 10) then /* 1 - Consultas */
					ie_classif_grupo_w	:= '1.';
					ie_classif_grupo_ww	:= '0.';
				elsif (nr_seq_grupo_ans_ww = 20) then /* 49 - Exames */
					ie_classif_grupo_w	:= '2.';
					ie_classif_grupo_ww	:= '0.';
				elsif (nr_seq_grupo_ans_ww = 30) then /* 51 - Terapias */
					ie_classif_grupo_w	:= '3.';
					ie_classif_grupo_ww	:= '0.';
				elsif (nr_seq_grupo_ans_ww = 41) then /* 7 - Internação - Honorário médico */
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '1.';
				elsif (nr_seq_grupo_ans_ww = 42) then /* 8 - Internação - Exames */
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '2.';
				elsif (nr_seq_grupo_ans_ww = 43) then /* 9 - Internação - Terapias*/
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '3.';
				elsif (nr_seq_grupo_ans_ww = 44) then /* 10 - Internação - Materiais médicos */
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '4.';
				elsif (nr_seq_grupo_ans_ww = 45) then /* 11 - Internação - Medicamentos */
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '5.';
				elsif (nr_seq_grupo_ans_ww = 49) then /* 12 - Internação - Outras despesas */
					ie_classif_grupo_w	:= '4.';
					ie_classif_grupo_ww	:= '9.';
				elsif (nr_seq_grupo_ans_ww = 50) then /* 6 - Outros atendimentos - Ambulatoriais */
					ie_classif_grupo_w	:= '5.';
					ie_classif_grupo_ww	:= '0.';
				elsif (nr_seq_grupo_ans_ww = 60) then /* 16 - Demais despesas assistenciais */
					ie_classif_grupo_w	:= '6.';
					ie_classif_grupo_ww	:= '0.';
				end if;

				/* 6 - Formação do preço */

				if (ie_preco_w = '1') then
					ie_classif_preco_w	:= '1.';
				elsif (ie_preco_w in ('2','3')) then
					ie_classif_preco_w	:= '2.';
				end if;

				/* 7 - Tipo relação */

				select	c.ie_tipo_relacao
				into STRICT	ie_tipo_relacao_w
				from	pls_conta		a,
					pls_protocolo_conta	b,
					pls_prestador		c
				where	a.nr_sequencia		= nr_seq_conta_w
				and	a.nr_seq_protocolo	= b.nr_sequencia
				and	a.nr_seq_prestador_exec	= c.nr_sequencia;

				if (ie_tipo_relacao_w = 'P') then
					ie_classif_relacao_w	:= '1.';
				elsif (ie_tipo_relacao_w in ('D','I')) then
					ie_classif_relacao_w	:= '2.';
				else
					ie_classif_relacao_w	:= '9.';
				end if;

				/* 8  - Contin. Plano Contas */

				/*
				0	FIXO no Caso de Consultas Exemas e Terapias  (Ambulatorio)
				1	Honorarios Médicos ¿ Internação
				2	Exames ¿ Internação
				3	Terapias ¿ Internação
				4	Materiais Médicos ¿ Internaçao
				5	Medicamentos ¿ Internação
				9	Outras despesas ¿ Internação
				*/
				/* 9 - Contin. Plano Contas */

				if (ie_ato_cooperado_w = '1') then
					ie_classif_ato_w	:= '1.';
				elsif (ie_ato_cooperado_w = '2') then
					ie_classif_ato_w	:= '2.';
				elsif (ie_ato_cooperado_w = '3') then
					ie_classif_ato_w	:= '7.';
				else
					ie_classif_ato_w	:= '0.';
				end if;

				/* 10 - Regulamentação */

				if (ie_regulamentacao_w	= 'R') then
					ie_classif_regulamentacao_w	:= '1.';
				else
					ie_classif_regulamentacao_w	:= '2.';
				end if;

				/* 11 - Tipo de contratação */

				if (ie_tipo_contratacao_w = 'I') then
					ie_classif_contratacao_w	:= '1.';
				elsif (ie_tipo_contratacao_w = 'CE') then
					ie_classif_contratacao_w	:= '3.';
				elsif (ie_tipo_contratacao_w = 'CA') then
					ie_classif_contratacao_w	:= '4.';
				end if;

				/* 12 e 13 - Segmentação */

				ie_classif_segmentacao_w	:= lpad(ie_segmentacao_w,2,'0');

				/*				1 ... 4				4		5			6			7			8		*/

				cd_classificacao_credito_w :=	cd_classificacao_credito_w ||cd_classif_conta_w	||ie_classif_grupo_w || 	ie_classif_preco_w ||	ie_classif_relacao_w ||	ie_classif_grupo_ww ||
				/*				9			10				11				12 e 13	*/

								ie_classif_ato_w ||	ie_classif_regulamentacao_w || 	ie_classif_contratacao_w || 	ie_classif_segmentacao_w;

				select	max(cd_conta_contabil)
				into STRICT	cd_conta_credito_w
				from	conta_contabil
				where	cd_classificacao	= cd_classificacao_credito_w
				and	ie_tipo			= 'A';

				/*			CONTA DÉBITO				*/

				cd_classificacao_debito_w	:= '1.2.3.1.1.';

				/*				1 ... 5				6			7 ... 9	*/

				cd_classificacao_debito_w :=	cd_classificacao_debito_w ||	ie_classif_preco_w ||	'3.' ||	'0.0.0.0.01'; --ie_classif_relacao_w || '1.0.0';
				select	max(cd_conta_contabil)
				into STRICT	cd_conta_debito_w
				from	conta_contabil
				where	cd_classificacao	= cd_classificacao_debito_w
				and	ie_tipo			= 'A';

				update	pls_conta_coparticipacao
				set	cd_conta_cred		= cd_conta_credito_w,
					cd_conta_deb		= cd_conta_debito_w,
					nr_seq_regra_ctb_cred	= nr_seq_regra_esquema_w,
					nr_seq_regra_ctb_deb	= nr_seq_regra_esquema_w,
					nr_seq_grupo_ans	= nr_seq_grupo_ans_w,
					cd_historico		= cd_historico_padrao_w,
					cd_historico_baixa	= cd_historico_baixa_w,
					cd_classif_cred		= cd_classificacao_credito_w,
					cd_classif_deb		= cd_classificacao_debito_w
				where	nr_sequencia		= nr_seq_conta_copartic_w;
				end;
			end loop;
			close C03;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_classif_mens ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

