SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_cobranca_prevista ( nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				 
ie_preco_w			varchar(1);


BEGIN 
 
begin 
select	c.ie_preco 
into STRICT	ie_preco_w 
from	pls_plano		c, 
	pls_segurado		b, 
	pls_conta		a 
where	a.nr_seq_segurado	= b.nr_sequencia 
and	b.nr_seq_plano		= c.nr_sequencia 
and	a.nr_sequencia		= nr_seq_conta_p;
exception 
when others then 
	--(-20011,'Não foi possível acessar as informações da conta selecionada! #@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(190975);
end;
 
if (ie_preco_w in ('2','3')) then 
--	(-20011,'Não é permitido gerar cobrança prevista para beneficiário com produto Pós-estabelecido! #@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(190976);	
end if;
insert into pls_conta_log(nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nm_usuario_alteracao, 
		dt_alteracao, 
		nr_seq_conta, 
		nr_seq_conta_proc, 
		ds_alteracao) 
	values (nextval('pls_conta_log_seq'), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_conta_p, 
		null, 
		'Cobrança prevista gerada para a conta: ' ||nr_seq_conta_p);
update	pls_conta 
set	ie_cobranca_prevista	= 'S', 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_sequencia		= nr_seq_conta_p;
 
update	pls_conta_proc 
set	ie_cobranca_prevista	= 'S', 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_seq_conta		= nr_seq_conta_p;
 
/* Calcular os valores novamente */
 
CALL pls_consistir_conta(nr_seq_conta_p, cd_estabelecimento_p, nm_usuario_p,'S',null,null,null,'S');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
