CREATE OR REPLACE PROCEDURE pls_gerar_comissao_cobr_emp ( nr_seq_cobranca_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_empresa_w		bigint;
pr_comissao_w			double precision;
vl_comissao_w			double precision;
vl_total_w			double precision;
vl_total_comissao_w		double precision;


BEGIN

select	max(nr_seq_empresa)
into STRICT	nr_seq_empresa_w
from	cobranca_escritural
where	nr_sequencia = nr_seq_cobranca_p;

select	max(pr_comissao),
	max(vl_comissao)
into STRICT	pr_comissao_w,
	vl_comissao_w
from	pls_desc_empresa_regra
where	nr_seq_empresa = nr_seq_empresa_w;

select	sum(vl_cobranca)
into STRICT	vl_total_w
from	titulo_receber_cobr
where	nr_seq_cobranca = nr_seq_cobranca_p;

vl_total_comissao_w := (coalesce(vl_total_w,0) * (coalesce(pr_comissao_w,0) / 100)) + coalesce(vl_comissao_w,0);

update	cobranca_escritural
set	vl_comissao_empresa = vl_total_comissao_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia = nr_seq_cobranca_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
