CREATE OR REPLACE PROCEDURE pls_gerar_comissao_estornada ( nr_seq_canal_venda_p bigint, nr_seq_comissao_p bigint, vl_comissao_total_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* Campos do cursor 1 */

nr_seq_estornado_w		bigint;
vl_estornado_w			double precision;
nr_seq_seg_estornado_w		bigint;
nr_seq_mens_seg_estornado_w	bigint;
nr_seq_vendedor_w		bigint;
vl_incremento_w			double precision;
nr_titulo_w			pls_comissao_beneficiario.nr_titulo%type;
nr_seq_regra_mensal_w		pls_comissao_beneficiario.nr_seq_regra_mensal%type;
nr_seq_proposta_w		pls_comissao_beneficiario.nr_seq_proposta%type;
cd_pessoa_fisica_w		pls_comissao_beneficiario.cd_pessoa_fisica%type;

/* Campos do cursor 2 */

tx_comissao_estornado_w		double precision;
ie_tipo_item_estornado_w	varchar(3);
vl_incremento_estornado_w	double precision;
vl_item_mens_estornado_w	double precision;
vl_comissao_estornado_w		double precision;
vl_premio_estornado_w		double precision;
nr_seq_plano_w			bigint;
nr_seq_plano_sca_w		bigint;
nr_seq_bonificacao_w		bigint;
nr_seq_regra_w			pls_comissao_benef_item.nr_seq_regra%type;
nr_seq_regra_equipe_w		pls_comissao_benef_item.nr_seq_regra_equipe%type;
nr_seq_vendedor_pf_w		pls_comissao_beneficiario.nr_seq_vendedor_pf%type;

vl_total_estornado_w		double precision;
nr_seq_benef_estornado_w	bigint;
tx_estorno_w			pls_estorno_comissao.tx_estorno%type;

C01 CURSOR FOR 	/* Filtrando benficiários estornados */
	SELECT	b.nr_sequencia,
		(c.vl_estorno * -1),
		b.nr_seq_segurado,
		b.nr_seq_segurado_mens,
		b.nr_seq_vendedor,
		(b.vl_incremento * -1),
		nr_titulo,
		b.nr_seq_regra_mensal,
		b.nr_seq_vendedor_pf,
		b.nr_seq_proposta,
		b.cd_pessoa_fisica,
		coalesce(c.tx_estorno,100) tx_estorno
	from	pls_estorno_comissao		c,
		pls_comissao_beneficiario	b,
		pls_comissao			a
	where	a.nr_sequencia = b.nr_seq_comissao
	and	a.nr_seq_canal_venda = c.nr_seq_canal_venda
	and	c.nr_seq_canal_venda = nr_seq_canal_venda_p
	and	b.nr_seq_segurado = c.nr_seq_segurado
	and	(b.dt_estorno IS NOT NULL AND b.dt_estorno::text <> '')
	and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
	and	coalesce(c.nr_seq_comissao_benef::text, '') = '';

C02 CURSOR FOR	/* Filtrando os itens dos benficiários estornados */
	SELECT	tx_comissao,
		ie_tipo_item,
		vl_incremento,
		vl_item_mensalidade,
		vl_comissao*-1,
		vl_premio,
		nr_seq_plano,
		nr_seq_plano_sca,
		nr_seq_bonificacao,
		nr_seq_regra,
		nr_seq_regra_equipe
	from	pls_comissao_benef_item
	where	nr_seq_comissao_benef = nr_seq_estornado_w;


BEGIN

vl_total_estornado_w := 0;

open C01;
loop
fetch C01 into
	nr_seq_estornado_w,
	vl_estornado_w,
	nr_seq_seg_estornado_w,
	nr_seq_mens_seg_estornado_w,
	nr_seq_vendedor_w,
	vl_incremento_w,
	nr_titulo_w,
	nr_seq_regra_mensal_w,
	nr_seq_vendedor_pf_w,
	nr_seq_proposta_w,
	cd_pessoa_fisica_w,
	tx_estorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('pls_comissao_beneficiario_seq')
	into STRICT	nr_seq_benef_estornado_w
	;

	insert	into	pls_comissao_beneficiario( nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_comissao, nr_seq_segurado_mens, nr_seq_segurado, vl_comissao_benef, vl_origem,
				vl_estorno, nr_seq_comissao_benef, nr_seq_vendedor, vl_incremento, nr_titulo,
				nr_seq_regra_mensal, nr_seq_vendedor_pf, nr_seq_proposta, cd_pessoa_fisica )
			values ( nr_seq_benef_estornado_w, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_comissao_p, nr_seq_mens_seg_estornado_w, nr_seq_seg_estornado_w, 0, 0,
				vl_estornado_w, nr_seq_estornado_w, nr_seq_vendedor_w, vl_incremento_w, nr_titulo_w,
				nr_seq_regra_mensal_w, nr_seq_vendedor_pf_w, nr_seq_proposta_w, cd_pessoa_fisica_w );

	vl_total_estornado_w := vl_total_estornado_w + vl_estornado_w;

	update	pls_estorno_comissao
	set	nr_seq_comissao_benef = nr_seq_benef_estornado_w
	where	nr_seq_canal_venda = nr_seq_canal_venda_p
	and	nr_seq_segurado = nr_seq_seg_estornado_w;

	open C02;
	loop
	fetch C02 into
		tx_comissao_estornado_w,
		ie_tipo_item_estornado_w,
		vl_incremento_estornado_w,
		vl_item_mens_estornado_w,
		vl_comissao_estornado_w,
		vl_premio_estornado_w,
		nr_seq_plano_w,
		nr_seq_plano_sca_w,
		nr_seq_bonificacao_w,
		nr_seq_regra_w,
		nr_seq_regra_equipe_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert	into	pls_comissao_benef_item(	nr_sequencia, nr_seq_comissao_benef, ie_tipo_item, vl_item_mensalidade,
					tx_comissao, vl_premio, vl_incremento, vl_comissao,
					vl_origem, nr_seq_plano, nr_seq_plano_sca, nr_seq_bonificacao, dt_atualizacao,
					nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_regra, nr_seq_regra_equipe )
				values(	nextval('pls_comissao_benef_item_seq'), nr_seq_benef_estornado_w, ie_tipo_item_estornado_w, vl_item_mens_estornado_w,
					tx_comissao_estornado_w, vl_premio_estornado_w, vl_incremento_estornado_w, ((vl_comissao_estornado_w*tx_estorno_w)/100),
					vl_comissao_estornado_w, nr_seq_plano_w, nr_seq_plano_sca_w, nr_seq_bonificacao_w, clock_timestamp(),
					nm_usuario_p, clock_timestamp(), nm_usuario_p, nr_seq_regra_w, nr_seq_regra_equipe_w );
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

update	pls_comissao
set	vl_comissao_canal = coalesce(vl_comissao_total_p,0) + coalesce(vl_total_estornado_w,0)
where	nr_sequencia = nr_seq_comissao_p;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_comissao_estornada ( nr_seq_canal_venda_p bigint, nr_seq_comissao_p bigint, vl_comissao_total_p bigint, nm_usuario_p text) FROM PUBLIC;

