CREATE OR REPLACE PROCEDURE pls_gerar_compl_cooperado ( nr_seq_cooperado_p bigint, nr_seq_compl_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		bigint;


BEGIN
select	coalesce(max(cd_pessoa_fisica),0)
into STRICT	cd_pessoa_fisica_w
from	pls_cooperado
where	nr_sequencia = nr_seq_cooperado_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	compl_pessoa_fisica
where	ie_tipo_complemento = '30'
and	cd_pessoa_fisica = cd_pessoa_fisica_w;

if (nr_sequencia_w = 0) then
	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	insert 	into compl_pessoa_fisica(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			ie_tipo_complemento,
			ds_observacao)
		values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			'30',
			obter_desc_expressao(779767));
end if;

nr_seq_compl_p	:= nr_sequencia_w;

commit;
end;
