CREATE OR REPLACE PROCEDURE pls_gerar_comunic_processo_jud ( nr_seq_processo_p bigint, ds_usuario_p text, ds_perfil_p text, ds_titulo_p text, nm_usuario_p text) AS $body$
DECLARE


ds_usuario_w		varchar(4000);
ds_perfil_w		varchar(4000);
ds_historico_w		varchar(4000);


BEGIN
ds_usuario_w	:= substr(ds_usuario_p, 1, length(ds_usuario_p) - 2);
ds_perfil_w	:= substr(ds_perfil_p, 1, length(ds_perfil_p) - 2);

select	substr(max(a.ds_historico),1,4000)
into STRICT	ds_historico_w
from	processo_judicial_hist	a
where	a.nr_seq_processo	= nr_seq_processo_p
and	a.dt_historico		=	(SELECT	max(x.dt_historico)
					from	processo_judicial_hist	x
					where	x.nr_seq_processo	= nr_seq_processo_p);

if (coalesce(ds_historico_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(255095);
end if;

insert into comunic_interna(dt_comunicado,
	ds_titulo,
	ds_comunicado,
	nm_usuario,
	dt_atualizacao,
	ie_geral,
	nm_usuario_destino,
	cd_perfil,
	nr_sequencia,
	ie_gerencial,
	nr_seq_classif,
	ds_perfil_adicional,
	cd_setor_destino,
	cd_estab_destino,
	ds_setor_adicional,
	dt_liberacao,
	ds_grupo,
	nm_usuario_oculto,
	nr_atendimento,
	ds_grupo_perfil,
	ds_usuarios_ocultos,
	nr_seq_agenda)
values (clock_timestamp(),
	ds_titulo_p,
	ds_historico_w,--'O protocolo ' || nr_seq_protocolo_p || ' est√° liberado pelo Itamed.',
	nm_usuario_p,
	clock_timestamp(),
	'N',
	ds_usuario_w,
	null,
	nextval('comunic_interna_seq'),
	'N',
	null,
	ds_perfil_w,
	null,
	null,
	null,
	clock_timestamp(),
	null,
	null,
	null,
	null,
	null,
	null);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
