CREATE OR REPLACE PROCEDURE pls_gerar_condic_pag_comiss ( nr_seq_canal_venda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_comissao_w	pls_comissao.nr_sequencia%type;
vl_repasse_w		pls_comissao.vl_comissao_canal%type;
qt_sem_regra_parc_w	integer;
cd_cond_pagto_w		pls_vendedor_regra_pag.cd_condicao_pagamento%type;

C01 CURSOR FOR 
	SELECT	nr_sequencia nr_seq_comissao, 
		vl_comissao_canal 
	from	pls_comissao 
	where	nr_seq_canal_venda = nr_seq_canal_venda_p 
	and	ie_regra_parcelamento = 'N';
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_comissao_w, 
	vl_repasse_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(cd_condicao_pagamento) 
	into STRICT	cd_cond_pagto_w 
	from	pls_vendedor_regra_pag 
	where	nr_seq_vendedor	= nr_seq_canal_venda_p 
	and	vl_repasse_w between coalesce(vl_comissao_inicial,vl_repasse_w) and coalesce(vl_comissao_final,vl_repasse_w);
	 
	if (cd_cond_pagto_w IS NOT NULL AND cd_cond_pagto_w::text <> '') then	 
		update	pls_comissao 
		set	ie_regra_parcelamento = 'S' 
		where	nr_sequencia = nr_seq_comissao_w;
		 
		qt_sem_regra_parc_w := pls_gerar_venc_comissao(null, nr_seq_comissao_w, nm_usuario_p, cd_estabelecimento_p, 'N', qt_sem_regra_parc_w);
	end if;		
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
