CREATE OR REPLACE PROCEDURE pls_gerar_conta_proc_face ( nr_seq_conta_proc_p bigint, cd_faces_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Gerar registros na tabela de faces do dente conforme os códigos de face que vieram no XML
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_faces_w	varchar(255)	:= null;
cd_face_dente_w	varchar(20);
i		integer	:= 1;


BEGIN

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	delete from pls_conta_proc_face_dente
	where nr_seq_conta_proc = nr_seq_conta_proc_p;

	if (cd_faces_p IS NOT NULL AND cd_faces_p::text <> '') then
		cd_faces_w	:= cd_faces_p;
		while(cd_faces_w IS NOT NULL AND cd_faces_w::text <> '') loop
			cd_face_dente_w	:= substr(cd_faces_w,1,1);

			insert into pls_conta_proc_face_dente(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nr_seq_conta_proc,
				cd_face_dente)
			values (nextval('pls_conta_proc_face_dente_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_conta_proc_p,
				cd_face_dente_w);

			i	:= i + 1;
			cd_faces_w	:= substr(cd_faces_w,i,length(cd_faces_w));
		end loop;
	end if;
end if;

/* Não pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
