SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_contrato_grupo ( nr_seq_contrato_p bigint, ie_contrato_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_abrangencia_w		varchar(10);
ie_preco_w			varchar(10);
nr_seq_regra_w			bigint;
nr_seq_grupo_contrato_w		bigint;
nr_seq_classificacao_w		bigint;
ie_tipo_contrato_w		varchar(10);
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;
nr_seq_grupo_intercambio_w	pls_intercambio.nr_seq_grupo_intercambio%type;

sg_estado_operadora_w		pessoa_juridica.sg_estado%type;
sg_estado_cooperativa_w		pessoa_juridica.sg_estado%type;
ie_tipo_intercambio_w		varchar(1);

C01 CURSOR FOR
	SELECT	b.ie_abrangencia,
		b.ie_preco,
		null,
		null,
		null
	from	pls_plano		b,
		pls_contrato_plano	a
	where	a.nr_seq_contrato	= nr_seq_contrato_p
	and	a.nr_seq_plano		= b.nr_sequencia
	and	b.ie_tipo_operacao	= 'B'
	and	ie_contrato_p		= 'O'
	
union

	SELECT	b.ie_abrangencia,
		b.ie_preco,
		b.nr_seq_classificacao,
		null,
		null
	from	pls_plano		b,
		pls_contrato_plano	a
	where	a.nr_seq_contrato	= nr_seq_contrato_p
	and	a.nr_seq_plano		= b.nr_sequencia
	and	b.ie_tipo_operacao	= 'A'
	and	ie_contrato_p		= 'O'
	
union	all

	select	b.ie_abrangencia,
		b.ie_preco,
		null,
		c.ie_tipo_contrato,
		c.nr_seq_grupo_intercambio
	from	pls_intercambio		c,
		pls_plano		b,
		pls_intercambio_plano	a
	where	a.nr_seq_intercambio	= c.nr_sequencia
	and	a.nr_seq_plano		= b.nr_sequencia
	and	c.nr_sequencia		= nr_seq_contrato_p
	and	ie_contrato_p		= 'I';


C02 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_grupo_contrato
	from	pls_regra_grupo_contrato	a
	where	a.ie_situacao	= 'A'
	and	a.ie_contrato	= ie_contrato_p
	and	((coalesce(a.ie_abrangencia::text, '') = '') or (a.ie_abrangencia = coalesce(ie_abrangencia_w, a.ie_abrangencia)))
	and	((coalesce(a.ie_preco::text, '') = '') or (a.ie_preco = coalesce(ie_preco_w, a.ie_preco)))
	and	((coalesce(a.nr_seq_classif_sca::text, '') = '') or (a.nr_seq_classif_sca = nr_seq_classificacao_w and (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '')))
	and	((coalesce(a.ie_tipo_contrato::text, '') = '') or (a.ie_tipo_contrato	= ie_tipo_contrato_w and (a.ie_tipo_contrato IS NOT NULL AND a.ie_tipo_contrato::text <> '')))
	and	((a.ie_tipo_intercambio = ie_tipo_intercambio_w) or (coalesce(a.ie_tipo_intercambio,'A') = 'A'))
	and	((a.nr_seq_grupo_intercambio = nr_seq_grupo_intercambio_w) or (coalesce(a.nr_seq_grupo_intercambio::text, '') = ''))
	order by	coalesce(a.ie_tipo_intercambio,' ');/* Ordenar para pegar a regra mais restritiva */
BEGIN

if (ie_contrato_p = 'I') then
	select	max(sg_estado)
	into STRICT	sg_estado_operadora_w
	from	pls_outorgante	a,
		pessoa_juridica	b
	where	a.cd_cgc_outorgante	= b.cd_cgc
	and	a.cd_estabelecimento	= cd_estabelecimento_p;

	select	max(sg_estado)
	into STRICT	sg_estado_cooperativa_w
	from	pls_intercambio	a,
		pls_congenere	b,
		pessoa_juridica	c
	where	a.nr_seq_congenere	= b.nr_sequencia
	and	b.cd_cgc		= c.cd_cgc
	and	a.nr_sequencia		= nr_seq_contrato_p;

	if (sg_estado_operadora_w = sg_estado_cooperativa_w) then
		ie_tipo_intercambio_w	:= 'E'; /* Estadual */
	else
		ie_tipo_intercambio_w	:= 'N'; /* Nacional */
	end if;
end if;


open C01;
loop
fetch C01 into
	ie_abrangencia_w,
	ie_preco_w,
	nr_seq_classificacao_w,
	ie_tipo_contrato_w,
	nr_seq_grupo_intercambio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_regra_w,
		nr_seq_grupo_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
	end;
end loop;
close C01;

if (ie_contrato_p	= 'O') then
	nr_seq_contrato_w	:= nr_seq_contrato_p;
elsif (ie_contrato_p	= 'I') then
	nr_seq_intercambio_w	:= nr_seq_contrato_p;
end if;

/*aaschlote - Não pode dar commit e verificar se nulo ao invés de colocar nvl*/

if (nr_seq_grupo_contrato_w IS NOT NULL AND nr_seq_grupo_contrato_w::text <> '') then
	insert into pls_preco_contrato(nr_sequencia, nr_seq_grupo, nr_seq_contrato,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, nr_seq_intercambio)
	values (	nextval('pls_preco_contrato_seq'), nr_seq_grupo_contrato_w, nr_seq_contrato_w,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, nr_seq_intercambio_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_contrato_grupo ( nr_seq_contrato_p bigint, ie_contrato_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

