CREATE OR REPLACE PROCEDURE pls_gerar_copartic_protocolo ( nm_usuario_p text, nr_SEQ_contrato_p bigint, dt_mes_competencia_p timestamp) AS $body$
DECLARE

nr_seq_conta_w		bigint;
nr_seq_protocolo_w	bigint;
nr_seq_conta_proc_w	bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
--exec pls_gerar_copartic_protocolo('TASY1804',null,null);
C01 CURSOR FOR
	SELECT  b.nr_sequencia,
		max(b.cd_estabelecimento)
	from    pls_segurado a,
		pls_conta b
	where   a.nr_seq_contrato = nr_seq_contrato_p
	and     a.nr_sequencia = b.nr_seq_segurado
	and     exists (SELECT  1
				from pls_conta_coparticipacao x
				where    x.nr_seq_conta = b.nr_sequencia
				and      coalesce(x.nr_seq_mensalidade_seg::text, '') = '')
	order by a.nr_sequencia desc;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_w;
--exec pls_gerar_copartic_protocolo('ATIVIA1303',null,null);	
BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_conta_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	open C02;
	loop
	fetch C02 into	
		nr_seq_conta_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		CALL pls_gerar_coparticipacao_proc(nr_seq_conta_proc_w,'N',nm_usuario_p);
		end;
	end loop;
	close C02;
	
	CALL pls_deletar_coparticipacao(nr_seq_conta_w,null,'S', 'N', null, null, nm_usuario_p,cd_estabelecimento_w);
	
	CALL pls_gerar_coparticipacao_conta(nr_seq_conta_w,'F',nm_usuario_p);
	
	update	pls_conta_coparticipacao
	set	ie_status_mensalidade = 'L',
		ie_status_coparticipacao = 'S'
	where	nr_seq_conta = nr_seq_conta_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
