CREATE OR REPLACE PROCEDURE pls_gerar_co_rec_glosa ( nr_seq_protocolo_rec_p pls_rec_glosa_protocolo.nr_sequencia%type, nr_seq_conta_rec_p pls_rec_glosa_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_conta_w		pls_conta.nr_sequencia%type;

C01 CURSOR(nr_seq_protocolo_rec_pc	pls_rec_glosa_protocolo.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_conta_rec,
		a.nr_seq_conta
	from	pls_rec_glosa_conta a
	where	a.nr_seq_protocolo	= nr_seq_protocolo_rec_pc
	and	a.ie_status		!= '3';

BEGIN

if (nr_seq_protocolo_rec_p IS NOT NULL AND nr_seq_protocolo_rec_p::text <> '') then
	for r_C01_w in C01(nr_seq_protocolo_rec_p) loop

		CALL pls_gerar_valor_co(	r_C01_w.nr_seq_conta, null, null,
					nm_usuario_p, cd_estabelecimento_p, r_C01_w.nr_seq_conta_rec);

	end loop;
elsif (nr_seq_conta_rec_p IS NOT NULL AND nr_seq_conta_rec_p::text <> '') then

	select	nr_seq_conta
	into STRICT	nr_seq_conta_w
	from	pls_rec_glosa_conta
	where	nr_sequencia = nr_seq_conta_rec_p;

	CALL pls_gerar_valor_co( nr_seq_conta_w, null, null, nm_usuario_p, cd_estabelecimento_p, nr_seq_conta_rec_p);
end if;

end;
$body$
