SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_dados_empregador_cat ( nr_seq_cat_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cgc_estipulante_w		varchar(14);
cd_pf_estipulante_w		varchar(10);
nr_seq_subestipulante_w		bigint;
ie_tipo_documento_w		smallint	:= 0;
cd_documento_w			varchar(14)	:= '';
nm_empregador_w			varchar(80);
ds_endereco_w			varchar(100);
ds_bairro_w			varchar(40);
cd_cep_w			varchar(15);
nm_municipio_w			varchar(100);
sg_estado_w			pessoa_juridica.sg_estado%type;
nr_telefone_w			varchar(15);
ds_complemento_w			varchar(40);


BEGIN

select	coalesce(cd_cgc_estipulante,''),
	coalesce(cd_pf_estipulante,''),
	coalesce(b.nr_seq_subestipulante,0)
into STRICT	cd_cgc_estipulante_w,
	cd_pf_estipulante_w,
	nr_seq_subestipulante_w
from	pls_contrato		c,
	pls_segurado		b,
	pls_cat			a
where	a.nr_seq_segurado	= b.nr_sequencia
and	b.nr_seq_contrato	= c.nr_sequencia
and	a.nr_sequencia		= nr_seq_cat_p;

if (nr_seq_subestipulante_w > 0) then
	select	coalesce(cd_cgc,''),
		coalesce(cd_pessoa_fisica,'')
	into STRICT	cd_cgc_estipulante_w,
		cd_pf_estipulante_w
	from	pls_sub_estipulante
	where	nr_sequencia	= nr_seq_subestipulante_w;
end if;

if (cd_cgc_estipulante_w IS NOT NULL AND cd_cgc_estipulante_w::text <> '') then
	ie_tipo_documento_w	:= 1;
	cd_documento_w		:= cd_cgc_estipulante_w;

	select	substr(ds_razao_social,1,80),
		ds_endereco,
		ds_bairro,
		cd_cep,
		ds_municipio,
		sg_estado,
		nr_telefone,
		substr(ds_complemento,1,40)
	into STRICT	nm_empregador_w,
		ds_endereco_w,
		ds_bairro_w,
		cd_cep_w,
		nm_municipio_w,
		sg_estado_w,
		nr_telefone_w,
		ds_complemento_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_estipulante_w;
elsif (cd_pf_estipulante_w IS NOT NULL AND cd_pf_estipulante_w::text <> '') then
	ie_tipo_documento_w	:= 3;
	
	select	a.nm_pessoa_fisica,
		a.nr_cpf,
		b.ds_endereco,
		substr(b.ds_bairro,1,40),
		b.cd_cep,
		b.ds_municipio,
		b.sg_estado,
		b.nr_telefone,
		substr(b.ds_complemento,1,40)
	into STRICT	nm_empregador_w,
		cd_documento_w,
		ds_endereco_w,
		ds_bairro_w,
		cd_cep_w,
		nm_municipio_w,
		sg_estado_w,
		nr_telefone_w,
		ds_complemento_w
	from	compl_pessoa_fisica	b,
		pessoa_fisica		a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.ie_tipo_complemento	= 1
	and	a.cd_pessoa_fisica	= cd_pf_estipulante_w;
end if;

if (ie_tipo_documento_w	> 0) then
	insert into pls_cat_empregador(nr_sequencia, nr_seq_cat, ie_tipo_documento,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, cd_cgc, cd_pessoa_fisica,
		cd_documento, nm_empregador, ds_endereco,
		ds_bairro, cd_cep, nm_municipio, 
		sg_estado, nr_telefone, ds_complemento)
	values (	nextval('pls_cat_empregador_seq'), nr_seq_cat_p, ie_tipo_documento_w,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, cd_cgc_estipulante_w, cd_pf_estipulante_w,
		cd_documento_w, nm_empregador_w, ds_endereco_w,
		ds_bairro_w, cd_cep_w, nm_municipio_w,
		sg_estado_w, nr_telefone_w, ds_complemento_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_dados_empregador_cat ( nr_seq_cat_p bigint, nm_usuario_p text) FROM PUBLIC;

