CREATE OR REPLACE PROCEDURE pls_gerar_dados_mov_benef_ted ( nr_seq_lote_mov_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
ie_tipo_movimentacao_w		varchar(10);
nr_seq_regra_ted_w		bigint;
qt_registros_w			bigint;


BEGIN 
 
select	ie_tipo_movimentacao 
into STRICT	ie_tipo_movimentacao_w 
from	pls_lote_mov_benef 
where	nr_sequencia	= nr_seq_lote_mov_p;
 
if (ie_tipo_movimentacao_w in ('T','I')) then 
	CALL pls_gerar_inclusao_mov_ted(nr_seq_lote_mov_p,nm_usuario_p,cd_estabelecimento_p);
end if;
 
if (ie_tipo_movimentacao_w in ('T','A')) then 
	CALL pls_gerar_alteracao_mov_ted(nr_seq_lote_mov_p,nm_usuario_p,cd_estabelecimento_p);
end if;
 
if (ie_tipo_movimentacao_w in ('T','E')) then 
	CALL pls_gerar_exclusao_mov_ted(nr_seq_lote_mov_p,nm_usuario_p,cd_estabelecimento_p);
end if;
 
if (ie_tipo_movimentacao_w = 'B') then 
	CALL pls_gerar_todos_benef_mov_ted(nr_seq_lote_mov_p,nm_usuario_p,cd_estabelecimento_p);
end if;
 
select	count(1) 
into STRICT	qt_registros_w 
from	PLS_MOVIMENTACAO_BENEF 
where	nr_seq_lote	= nr_seq_lote_mov_p;
 
if (qt_registros_w >0) then 
	update	PLS_LOTE_MOV_BENEF 
	set	DT_GERACAO_LOTE	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_lote_mov_p;
else 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(236706,'');
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
