CREATE OR REPLACE PROCEDURE pls_gerar_dados_procedimento ( nr_seq_prestador_p bigint, ie_tipo_guia_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_regra_p INOUT bigint, cd_medico_executor_p INOUT text, nr_seq_grau_partic_p INOUT bigint) AS $body$
DECLARE


cd_grupo_proc_w		bigint;
cd_especialidade_w	integer;
cd_area_procedimento_w	integer;
nr_seq_regra_w		bigint	:= 0;
cd_medico_executor_w	varchar(10);
nr_seq_grau_partic_w	bigint;
ie_estrutura_w		varchar(1)	:= 'N';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_medico_executor,
		nr_seq_grau_partic,
		ie_estrutura
	from	pls_gerar_dados_proc
	where	nr_seq_prestador	= nr_seq_prestador_p
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(cd_procedimento,cd_procedimento_p) 	= cd_procedimento_p
	and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced,ie_origem_proced_p)= ie_origem_proced_p))
	and	coalesce(cd_area_procedimento,cd_area_procedimento_w)	= cd_area_procedimento_w
	and	coalesce(cd_especialidade,cd_especialidade_w)		= cd_especialidade_w
	and	coalesce(cd_grupo_proc,cd_grupo_proc_w)			= cd_grupo_proc_w
	and	coalesce(ie_tipo_guia, ie_tipo_guia_p)			= ie_tipo_guia_p
	and	ie_situacao	= 'A'
	order by
		coalesce(cd_procedimento,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);


BEGIN

/* obter estrutura do procedimento */

begin
select	coalesce(cd_grupo_proc,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_area_procedimento,0)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	cd_grupo_proc_w		:= 0;
	cd_especialidade_w	:= 0;
	cd_area_procedimento_w	:= 0;
end;

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	cd_medico_executor_w,
	nr_seq_grau_partic_w,
	ie_estrutura_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_estrutura_w	= 'N') then
		nr_seq_regra_w		:= 0;
		cd_medico_executor_w	:= '';
		nr_seq_grau_partic_w	:= '';
	end if;
	end;
end loop;
close C01;

nr_seq_regra_p		:= nr_seq_regra_w;
cd_medico_executor_p	:= cd_medico_executor_w;
nr_seq_grau_partic_p	:= nr_seq_grau_partic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
