CREATE OR REPLACE PROCEDURE pls_gerar_deflator_lote_col ( nr_seq_lote_reajuste_p bigint, tx_deflator_p bigint, nr_seq_motivo_def_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, tx_reajuste_correto_p bigint, tx_reajuste_copart_p bigint, tx_reajuste_copart_max_p bigint, tx_deflator_copartic_p bigint, tx_deflator_copartic_max_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
nr_seq_lote_novo_reajuste_w	bigint;
nr_seq_motivo_def_w		bigint;
nr_seq_reajsute_w		bigint;
nr_seq_lote_novo_aux_w		bigint;
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;
			
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_reajuste 
	where	nr_seq_lote_referencia	= nr_seq_lote_reajuste_p;


BEGIN 
 
select	max(nr_seq_contrato), 
	max(nr_seq_intercambio) 
into STRICT	nr_seq_contrato_w, 
	nr_seq_intercambio_w 
from	pls_reajuste 
where	nr_sequencia	= nr_seq_lote_reajuste_p;
 
if (coalesce(nr_seq_motivo_def_p,0) = 0) then 
	nr_seq_motivo_def_w	:= null;
else 
	nr_seq_motivo_def_w	:= nr_seq_motivo_def_p;
end if;	
 
if	((coalesce(nr_seq_contrato_w,0) <> 0) or (coalesce(nr_seq_intercambio_w,0) <> 0)) then 
	nr_seq_lote_novo_aux_w := PLS_Gerar_lote_deflator(null, nr_seq_lote_reajuste_p, tx_deflator_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_motivo_def_p, ds_observacao_p, 'N', tx_reajuste_correto_p, tx_reajuste_copart_p, tx_reajuste_copart_max_p, tx_deflator_copartic_p, tx_deflator_copartic_max_p, nr_seq_lote_novo_aux_w);
else 
	select	nextval('pls_reajuste_seq') 
	into STRICT	nr_seq_lote_novo_reajuste_w 
	;
 
	insert	into	pls_reajuste(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec, 
			dt_reajuste,tx_reajuste,ie_indice_reajuste,ds_oficio_ans,nr_protocolo_ans, 
			ie_tipo_reajuste,ds_observacao,ie_status,nr_seq_contrato,cd_estabelecimento, 
			ie_reajuste_tabela,dt_autorizacao_ans,tx_deflator,nr_seq_motivo_deflator,nr_seq_intercambio, 
			nr_contrato,ie_aplicacao_reajuste,nr_seq_plano,ie_tipo_contrato, 
			dt_reajuste_aux) 
	(SELECT		nr_seq_lote_novo_reajuste_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p, 
			dt_reajuste,0,ie_indice_reajuste,ds_oficio_ans,nr_protocolo_ans, 
			ie_tipo_reajuste,'Reajuste deflator referente ao lote ' || to_char(nr_sequencia) || chr(10) || ds_observacao_p,'1',nr_seq_contrato,cd_estabelecimento, 
			ie_reajuste_tabela,dt_autorizacao_ans,tx_deflator_p,nr_seq_motivo_def_w,nr_seq_intercambio, 
			nr_contrato,ie_aplicacao_reajuste,nr_seq_plano,ie_tipo_contrato, 
			to_char(dt_reajuste,'mm/yyyy') 
		from	pls_reajuste 
		where	nr_sequencia = nr_seq_lote_reajuste_p);
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_reajsute_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		nr_seq_lote_novo_aux_w := PLS_Gerar_lote_deflator(nr_seq_lote_novo_reajuste_w, nr_seq_reajsute_w, tx_deflator_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_motivo_def_p, ds_observacao_p, 'N', tx_reajuste_correto_p, tx_reajuste_copart_p, tx_reajuste_copart_max_p, tx_deflator_copartic_p, tx_deflator_copartic_max_p, nr_seq_lote_novo_aux_w);
		 
		end;
	end loop;
	close C01;
	 
	update	pls_reajuste 
	set	IE_STATUS		= '3' 
	where	nr_sequencia		= nr_seq_lote_novo_reajuste_w;
 
	update	pls_reajuste 
	set	nr_seq_lote_deflator	= nr_seq_lote_novo_reajuste_w 
	where	nr_sequencia		= nr_seq_lote_reajuste_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
