CREATE OR REPLACE PROCEDURE pls_gerar_demonstrativo_pag ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_rec_p pls_rec_glosa_protocolo.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_usuario_web_p pls_regra_demons_pagto.nr_seq_usuario%type, dt_pagamento_p pls_rel_pag_prestador.dt_pagamento%type, dt_competencia_p pls_rel_pag_prestador.dt_competencia%type, nr_seq_lote_p pls_lote_pagamento.nr_sequencia%type, cpf_prestador_p pls_rel_pag_prestador.cpf_prestador%type, cnpj_prestador_p pls_rel_pag_prestador.cnpj_prestador%type, ie_tipo_demonstrativo_p pls_rel_pag_prestador.ie_tipo_demonstrativo%type, cd_prestador_p pls_rel_pag_prestador.cd_prestador%type, nm_usuario_p usuario.nm_usuario%type, ie_funcao_pagamento_p pls_parametro_pagamento.ie_funcao_pagamento%type, nr_seq_versao_p INOUT pls_rel_pprest_versao.nr_sequencia%type) AS $body$
DECLARE

					
ie_funcao_pagamento_w	pls_parametro_pagamento.ie_funcao_pagamento%type;
dt_competencia_w	timestamp := dt_competencia_p;


BEGIN

if (to_char(dt_competencia_w,'dd/mm/rrrr') = to_char('02/03/2020')) then -- problema na hora de converter "202002"
	dt_competencia_w := trunc(dt_competencia_w) - 2;
end if;

if (coalesce(ie_funcao_pagamento_p::text, '') = '') then
	ie_funcao_pagamento_w	:= pls_obter_tipo_pagamento(nr_seq_prestador_p, dt_competencia_w);
else
	ie_funcao_pagamento_w	:= ie_funcao_pagamento_p;
end if;

nr_seq_versao_p := pls_demonstrativo_pgto_pck.gerar_demonstrativo(	nr_seq_protocolo_p, nr_seq_protocolo_rec_p, nr_seq_prestador_p, nr_seq_usuario_web_p, dt_pagamento_p, dt_competencia_w, nr_seq_lote_p, cpf_prestador_p, cnpj_prestador_p, ie_tipo_demonstrativo_p, cd_prestador_p, nm_usuario_p, ie_funcao_pagamento_w, nr_seq_versao_p);
						
end;
$body$
