CREATE OR REPLACE PROCEDURE pls_gerar_devolucao_alt_plano ( nr_seq_alt_plano_p bigint, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
nr_seq_plano_ant_w	pls_plano.nr_sequencia%type;
dt_alteracao_w		pls_segurado_alt_plano.dt_alteracao%type;
nr_seq_tabela_nova_w	pls_segurado_alt_plano.nr_seq_tabela_atual%type;
nr_seq_segurado_preco_w	pls_segurado_preco.nr_sequencia%type;
vl_preco_atual_w	pls_segurado_preco.vl_preco_atual%type;
vl_plano_anterior_w	pls_mensalidade_seg_item.vl_item%type;
vl_preco_mens_atual_w	pls_segurado_preco.vl_preco_atual%type;
qt_dias_diferenca_w	bigint;
ie_diferenca_w		varchar(1);
qt_regra_w		integer;
nr_seq_motivo_w		pls_regra_lanc_alt_plano.nr_seq_motivo%type;
vl_diferenca_w		double precision;
dt_lancamento_w		timestamp;
dt_mensalidade_w	timestamp;
qt_mensalidade_w	integer;

C01 CURSOR FOR
	SELECT	sum(a.vl_item) vl_item,
		a.dt_inicio_cobertura,
		a.dt_fim_cobertura,
		(a.dt_fim_cobertura - a.dt_inicio_cobertura)+1 qt_dias_cobertura
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b,
		pls_mensalidade			c
	where	b.nr_sequencia		= a.nr_seq_mensalidade_seg
	and	c.nr_sequencia		= b.nr_seq_mensalidade
	and	a.ie_tipo_item		in ('1','12')
	and	coalesce(c.ie_cancelamento::text, '') = ''
	and	b.nr_seq_segurado	= nr_seq_segurado_w
	and	a.dt_fim_cobertura	> dt_alteracao_w
	group by a.dt_inicio_cobertura,
		a.dt_fim_cobertura;

C02 CURSOR FOR
	SELECT	nr_seq_motivo
	from	pls_regra_lanc_alt_plano
	where	ie_diferenca_valor	= ie_diferenca_w;
BEGIN

select	count(1)
into STRICT	qt_regra_w
from	pls_regra_lanc_alt_plano;

if (qt_regra_w > 0) then
	select	nr_seq_segurado,
		nr_seq_plano_ant,
		trunc(dt_alteracao,'dd'),
		nr_seq_tabela_atual
	into STRICT	nr_seq_segurado_w,
		nr_seq_plano_ant_w,
		dt_alteracao_w,
		nr_seq_tabela_nova_w
	from	pls_segurado_alt_plano
	where	nr_sequencia	= nr_seq_alt_plano_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_preco_w
	from	pls_segurado_preco
	where	nr_seq_segurado	= nr_seq_segurado_w
	and	nr_seq_tabela	= nr_seq_tabela_nova_w;

	select	max(vl_preco_atual) + max(vl_adaptacao)
	into STRICT	vl_preco_atual_w
	from	pls_segurado_preco
	where	nr_sequencia	= nr_seq_segurado_preco_w;

	for r_c01_w in C01 loop
		begin
		if (r_c01_w.dt_inicio_cobertura > dt_alteracao_w) then
			qt_dias_diferenca_w	:= r_c01_w.qt_dias_cobertura;
		else
			qt_dias_diferenca_w	:= (r_c01_w.dt_fim_cobertura - dt_alteracao_w) + 1;
		end if;

		vl_plano_anterior_w	:= (r_c01_w.vl_item / r_c01_w.qt_dias_cobertura) * qt_dias_diferenca_w;
		vl_preco_mens_atual_w	:= (vl_preco_atual_w / r_c01_w.qt_dias_cobertura) * qt_dias_diferenca_w;
		vl_diferenca_w		:= vl_preco_mens_atual_w - vl_plano_anterior_w;

		if (vl_diferenca_w > 0) then
			ie_diferenca_w	:= '1';
		else
			ie_diferenca_w	:= '2';
		end if;

		nr_seq_motivo_w	:= null;
		for r_c02_w in C02 loop
			begin
			nr_seq_motivo_w	:= r_c02_w.nr_seq_motivo;
			end;
		end loop;

		if (nr_seq_motivo_w IS NOT NULL AND nr_seq_motivo_w::text <> '') then
			dt_lancamento_w		:= null;
			dt_mensalidade_w	:= trunc(dt_alteracao_w,'month');
			while(coalesce(dt_lancamento_w::text, '') = '') loop
				begin
				select	count(1)
				into STRICT	qt_mensalidade_w
				from	pls_mensalidade_segurado a,
					pls_mensalidade b
				where	b.nr_sequencia		= a.nr_seq_mensalidade
				and	coalesce(b.ie_cancelamento::text, '') = ''
				and	a.nr_seq_segurado	= nr_seq_segurado_w
				and	a.dt_mesano_referencia	= dt_mensalidade_w;

				if (qt_mensalidade_w = 0) then
					dt_lancamento_w		:= dt_mensalidade_w;
				else
					dt_mensalidade_w	:= add_months(dt_mensalidade_w,1);
				end if;
				end;
			end loop;

			insert	into	pls_segurado_mensalidade(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					dt_referencia, nr_seq_segurado, ie_tipo_item, nr_seq_motivo, vl_item,
					ie_tipo_lanc, ie_situacao, nr_seq_alt_plano, cd_estabelecimento)
				values (	nextval('pls_segurado_mensalidade_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
					dt_lancamento_w, nr_seq_segurado_w, '20', nr_seq_motivo_w, vl_diferenca_w,
					'B', 'A', nr_seq_alt_plano_p, cd_estabelecimento_p);
		end if;

		end;
	end loop; --C01
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_devolucao_alt_plano ( nr_seq_alt_plano_p bigint, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

