CREATE OR REPLACE PROCEDURE pls_gerar_devolucao_sib ( nr_seq_lote_p bigint, ie_tipo_devolucao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_tipo_devolucao_p	= 'E') then 
	CALL sib_importar_dev_erro(nr_seq_lote_p,cd_estabelecimento_p,nm_usuario_p);
	update	pls_lote_sib 
	set	ie_erro_importado	= 'S' 
	where	nr_sequencia		= nr_seq_lote_p;
	 
elsif (ie_tipo_devolucao_p	= 'R') then 
	CALL sib_importar_devolucao_resumo(nr_seq_lote_p,nm_usuario_p);
	update	pls_lote_sib 
	set	ie_resumo_importado	= 'S' 
	where	nr_sequencia		= nr_seq_lote_p;
elsif (ie_tipo_devolucao_p	= 'C') then 
	CALL sib_importar_dev_cco(nr_seq_lote_p,cd_estabelecimento_p,nm_usuario_p);
	update	pls_lote_sib 
	set	ie_cco_importado	= 'S' 
	where	nr_sequencia		= nr_seq_lote_p;
end if;
 
delete from w_sib_devolucao;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
