SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_diagnostico ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_cid_p cid_doenca.cd_doenca_cid%type, nm_usuario_p usuario.nm_usuario%type, ie_classificacao_p pls_diagnostico_conta.ie_classificacao%type default 'P') AS $body$
DECLARE
	
			
/*ie_classificacao_p pode ser P para o principal ou S para os secundarios*/
			
			
nr_seq_diagnostico_w	pls_diagnostico_conta.nr_sequencia%type;			
cd_cid_w		cid_doenca.cd_doenca_cid%type;
qt_cid_w		integer;


BEGIN

select	count(1)
into STRICT	qt_cid_w
from	cid_doenca
where	cd_doenca_cid	= cd_cid_p;

if (qt_cid_w > 0) then
	cd_cid_w := cd_cid_p;
end if;

if (cd_cid_p IS NOT NULL AND cd_cid_p::text <> '') then	
		
	if ( ie_classificacao_p = 'P' or coalesce(ie_classificacao_p::text, '') = '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_diagnostico_w
		from	pls_diagnostico_conta
		where	ie_classificacao = 'P'
		and	nr_seq_conta	 = nr_seq_conta_p;
		
		if (coalesce(nr_seq_diagnostico_w::text, '') = '') then	
			insert into pls_diagnostico_conta(nr_sequencia, nm_usuario, dt_atualizacao,
				 cd_doenca, nr_seq_conta, ie_classificacao,
				 cd_doenca_imp)
			values (nextval('pls_diagnostico_conta_seq'), nm_usuario_p, clock_timestamp(),  
				 cd_cid_w, nr_seq_conta_p, 'P',
				 cd_cid_p);			
		else			
			update	pls_diagnostico_conta
			set	cd_doenca	= cd_cid_w,
				cd_doenca_imp	= cd_cid_p
			where	nr_seq_conta	= nr_seq_conta_p
			and	ie_classificacao = 'P';		
		end if;	
	else --se for um dos CID secundarios apenas insere
		insert into pls_diagnostico_conta(nr_sequencia, nm_usuario, dt_atualizacao,
			 cd_doenca, nr_seq_conta, ie_classificacao,
			 cd_doenca_imp)
		values (nextval('pls_diagnostico_conta_seq'), nm_usuario_p, clock_timestamp(),  
			 cd_cid_w, nr_seq_conta_p, 'S',
			 cd_cid_p);
	end if;
else
	delete	from pls_diagnostico_conta
	where	nr_seq_conta = nr_seq_conta_p
	and	ie_classificacao = 'P';
end if;

commit;
		
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
