CREATE OR REPLACE PROCEDURE pls_gerar_diarias_sip_conta ( nr_seq_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_guia_w		varchar(20);
cd_guia_referencia_w	varchar(20);
qt_diarias_w		integer	:= 0;


BEGIN

select	coalesce(cd_guia,'X'),
	coalesce(cd_guia_referencia,'X')
into STRICT	cd_guia_w,
	cd_guia_referencia_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;

if (cd_guia_w	= cd_guia_referencia_w) then
	/* SOMAR OS DIAS UTILIZADOS DA GUIA PRÃ“PRIA GUIA */

	select	coalesce(max(qt_dia_relacionada),0)
	into STRICT	qt_diarias_w
	from	pls_conta_autor
	where	nr_sequencia	= (SELECT max(nr_sequencia) from pls_conta_autor where nr_seq_conta = nr_seq_conta_p);
elsif (cd_guia_w	<> cd_guia_referencia_w) then
	/* SOMAR OS DIAS UTILIZADOS DA GUIA DE REFERENCIA */

	select	coalesce(max(b.qt_dia_relacionada),0)
	into STRICT	qt_diarias_w
	from	pls_conta_autor	b,
		pls_conta	a
	where	a.nr_sequencia	= b.nr_seq_conta
	and	a.cd_guia	= cd_guia_referencia_w
	and	b.nr_sequencia	= (SELECT max(x.nr_sequencia) from pls_conta_autor x where x.nr_seq_conta = a.nr_sequencia);
end if;

update	pls_conta
set	qt_diarias_sip	= qt_diarias_w
where	nr_sequencia	= nr_seq_conta_p;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
