SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_documentos_proposta ( nr_seq_proposta_p bigint, nr_seq_instrumento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_instrumento_w	bigint := 0;
ds_instrumento_w	varchar(255);
nr_seq_documento_w	bigint;
nr_seq_plano_instr_w	bigint;
cd_classificacao_w	varchar(20);
nm_dispositivo_w	varchar(255);
ds_tema_w		text;
ie_gerar_titulo_w	varchar(1);
ie_quebra_pagina_w	varchar(1);
nr_seq_plano_w		bigint;
nr_seq_doc_texto_w	bigint;
dt_proposta_w		timestamp;
qt_registro_w		bigint;
nr_seq_tipo_tema_w	pls_contrato_doc_texto.nr_seq_tipo_tema%type;

C00 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_proposta_plano
	where	nr_seq_proposta	= nr_seq_proposta_p;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_instrumento
	from	pls_instrumento
	where	nr_seq_plano	= nr_seq_plano_w
	and	((nr_Sequencia = nr_seq_instrumento_p) or (coalesce(nr_seq_instrumento_p,0) = 0))
	and	trunc(dt_proposta_w,'dd') >= trunc(coalesce(dt_inicio_vigencia,dt_proposta_w),'dd')
	and	trunc(dt_proposta_w,'dd') <= trunc(coalesce(dt_fim_vigencia,dt_proposta_w),'dd')
	and	ie_situacao	= 'A'
	order by	nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_classificacao,
		nm_dispositivo,
		ie_gerar_titulo,
		ie_quebra_pagina,
		nr_seq_tipo_tema
	from	pls_plano_instrumento
	where	nr_seq_instrumento	= nr_seq_instrumento_w
	and	coalesce(ie_permite_inclusao_termo,'S') = 'S';


BEGIN

select	dt_inicio_proposta
into STRICT	dt_proposta_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

if (coalesce(nr_seq_instrumento_p,0) = 0) then
	delete	FROM pls_contrato_doc_texto
	where	nr_seq_documento in (	SELECT	nr_sequencia
					from	pls_contrato_documento
					where	nr_seq_proposta	= nr_seq_proposta_p);
	
	delete	FROM pls_contrato_documento
	where	nr_seq_proposta	= nr_seq_proposta_p;
end if;

open C00;
loop
fetch C00 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin
	
	open C01;
	loop
	fetch C01 into
		nr_seq_instrumento_w,
		ds_instrumento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_instrumento_w IS NOT NULL AND nr_seq_instrumento_w::text <> '') then
			
			select	count(*)
			into STRICT	qt_registro_w
			from	pls_contrato_documento
			where	nr_seq_proposta		= nr_seq_proposta_p
			and	nr_seq_instrumento	= nr_seq_instrumento_w;
			
			if (qt_registro_w = 0) then
				select  nextval('pls_contrato_documento_seq')
				into STRICT    nr_seq_documento_w
				;
				
				insert into pls_contrato_documento(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_proposta,nr_seq_instrumento,ds_documento, nr_seq_plano)
				values (	nr_seq_documento_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_proposta_p,nr_seq_instrumento_w,ds_instrumento_w, nr_seq_plano_w);
				
				open C02;
				loop
				fetch C02 into
					nr_seq_plano_instr_w,
					cd_classificacao_w,
					nm_dispositivo_w,
					ie_gerar_titulo_w,
					ie_quebra_pagina_w,
					nr_seq_tipo_tema_w;
				EXIT WHEN NOT FOUND; /* apply on C02 */
					begin
					select	nextval('pls_contrato_doc_texto_seq')
					into STRICT	nr_seq_doc_texto_w
					;
					
					insert into pls_contrato_doc_texto(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
							nr_seq_documento,cd_classificacao,ds_dispositivo,ie_converte_macro,
							ie_imprimir_titulo,ie_quebra_pagina,nr_seq_tipo_tema)
					values (	nr_seq_doc_texto_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
							nr_seq_documento_w,cd_classificacao_w,nm_dispositivo_w,'N',
							ie_gerar_titulo_w,ie_quebra_pagina_w,nr_seq_tipo_tema_w);
					
					CALL COPIA_CAMPO_LONG_DE_PARA('PLS_PLANO_INSTRUMENTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_plano_instr_w,
							'PLS_CONTRATO_DOC_TEXTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_doc_texto_w);
					
					CALL pls_gerar_texto_macro(nr_seq_doc_texto_w, null, nr_seq_proposta_p, nm_usuario_p, 'P');
					end;
				end loop;
				close C02;
			end if;
		end if;
		end;
	end loop;
	close C01;
	end;
end loop;
close C00;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_documentos_proposta ( nr_seq_proposta_p bigint, nr_seq_instrumento_p bigint, nm_usuario_p text) FROM PUBLIC;

