SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_documento_conta ( nr_seq_conta_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_documento_w		pls_documento.nr_sequencia%type;
nr_seq_protocolo_w		varchar(4000);
nr_seq_regra_w			varchar(255);
nr_protocolo_prestador_w	pls_protocolo_conta.nr_protocolo_prestador%type;
nr_prestador_protocolo_w	pls_protocolo_conta.nr_seq_prestador%type;


BEGIN
nr_seq_protocolo_w	:= nr_seq_conta_p;

while(position(',' in nr_seq_protocolo_w) <> 0) loop
	begin
	--obtem-se a primeira regra
	nr_seq_regra_w	:= substr(nr_seq_protocolo_w,1,position(',' in nr_seq_protocolo_w)-1);
	--remove-se essa do conjunto
	nr_seq_protocolo_w	:= substr(nr_seq_protocolo_w,position(',' in nr_seq_protocolo_w)+1,4000);

	select	nextval('pls_documento_seq')
	into STRICT	nr_seq_documento_w
	;

	select	max(a.nr_protocolo_prestador),
		max(a.nr_seq_prestador)
	into STRICT	nr_protocolo_prestador_w,
		nr_prestador_protocolo_w
	from	pls_protocolo_conta	a,
		pls_conta		b
	where	a.nr_sequencia	= b.nr_seq_protocolo
	and	b.nr_sequencia	= nr_seq_regra_w;

	insert into  pls_documento(nr_sequencia, nr_documento, ie_tipo_guia,
		nr_seq_prestador, nr_seq_segurado, nm_usuario,
		dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec,
		cd_estabelecimento, dt_emissao, nr_seq_protocolo,
		nr_seq_conta, ie_status, nr_lote_protocolo,
		nr_seq_prestador_prot)
	(SELECT	nr_seq_documento_w, cd_guia, ie_tipo_guia,
		nr_seq_prestador_exec, nr_seq_segurado, nm_usuario_p,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		cd_estabelecimento_p, dt_emissao, nr_seq_protocolo,
		nr_sequencia,'A', nr_protocolo_prestador_w,
		nr_prestador_protocolo_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_regra_w
	and	(cd_guia IS NOT NULL AND cd_guia::text <> ''));
	end;

	CALL pls_imp_glosas_documentos(nr_seq_regra_w, nr_seq_documento_w, nm_usuario_p);

end loop;

/*commit;*/

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
