SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_documento_contrato ( nr_seq_contrato_p bigint, nr_seq_instrumento_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_instrumento_w		bigint := 0;
ds_instrumento_w		varchar(255);
nr_seq_documento_w		bigint;
nr_seq_plano_instr_w		bigint;
cd_classificacao_w		varchar(20);
nm_dispositivo_w		varchar(255);
ds_tema_w			text;
ie_gerar_titulo_w		varchar(1);
ie_quebra_pagina_w		varchar(1);
nr_seq_plano_w			bigint;
nr_seq_doc_texto_w		bigint;
dt_contrato_w			timestamp;
nr_seq_w_copia_long_w		bigint;
ds_tema_char_w			varchar(32764);
qt_registro_w			bigint;
nr_seq_tipo_tema_w		pls_plano_instrumento.nr_seq_tipo_tema%type;

C00 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_contrato_plano
	where	nr_seq_contrato = nr_seq_contrato_p
	and	ie_situacao = 'A';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_instrumento
	from	pls_instrumento
	where	nr_seq_plano	= nr_seq_plano_w
	and	((nr_sequencia(nr_seq_instrumento_p)) or (coalesce(nr_seq_instrumento_p::text, '') = ''))
	and	trunc(dt_contrato_w,'dd') >= trunc(coalesce(dt_inicio_vigencia,dt_contrato_w),'dd')
	and	trunc(dt_contrato_w,'dd') <= trunc(coalesce(dt_fim_vigencia,dt_contrato_w),'dd')
	and	ie_situacao	= 'A'
	order by	nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_classificacao,
		nm_dispositivo,
		ie_gerar_titulo,
		ie_quebra_pagina,
		nr_seq_tipo_tema
	from	pls_plano_instrumento
	where	nr_seq_instrumento	= nr_seq_instrumento_w
	and	coalesce(ie_permite_inclusao_termo,'S') = 'S';


BEGIN

select	dt_contrato
into STRICT	dt_contrato_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (coalesce(nr_seq_instrumento_p,0) = 0) then
	delete	from pls_contrato_doc_texto
	where	nr_seq_documento in (	SELECT	nr_sequencia
					from	pls_contrato_documento
					where	nr_seq_contrato = nr_seq_contrato_p);
	
	delete	from pls_contrato_documento
	where	nr_seq_contrato = nr_seq_contrato_p;
end if;

open C00;
loop
fetch C00 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_instrumento_w,
		ds_instrumento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_instrumento_w IS NOT NULL AND nr_seq_instrumento_w::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	pls_contrato_documento
			where	nr_seq_contrato		= nr_seq_contrato_p
			and	nr_seq_instrumento	= nr_seq_instrumento_w;
			
			if (qt_registro_w = 0) then
				select	nextval('pls_contrato_documento_seq')
				into STRICT	nr_seq_documento_w
				;
				
				insert into pls_contrato_documento(	nr_sequencia, dt_atualizacao, nm_usuario,
						nr_seq_contrato, nr_seq_instrumento, ds_documento,
						nr_seq_plano,dt_atualizacao_nrec, nm_usuario_nrec)
				values (	nr_seq_documento_w, clock_timestamp(), nm_usuario_p,
						nr_seq_contrato_p, nr_seq_instrumento_w, ds_instrumento_w,
						nr_seq_plano_w, clock_timestamp(), nm_usuario_p);
				
				open C02;
				loop
				fetch C02 into
					nr_seq_plano_instr_w,
					cd_classificacao_w,
					nm_dispositivo_w,
					ie_gerar_titulo_w,
					ie_quebra_pagina_w,
					nr_seq_tipo_tema_w;
				EXIT WHEN NOT FOUND; /* apply on C02 */
					begin
					
					select	nextval('pls_contrato_doc_texto_seq')
					into STRICT	nr_seq_doc_texto_w
					;
					
					insert	into	pls_contrato_doc_texto(	nr_sequencia, dt_atualizacao, nm_usuario,
							nr_seq_documento, cd_classificacao, ds_dispositivo,
							ds_tema, dt_atualizacao_nrec, nm_usuario_nrec,
							ie_converte_macro, ie_imprimir_titulo, ie_quebra_pagina,
							nr_seq_tipo_tema)
						values ( nr_seq_doc_texto_w, clock_timestamp(),nm_usuario_p,
							nr_seq_documento_w, cd_classificacao_w, nm_dispositivo_w,
							ds_tema_w, clock_timestamp(), nm_usuario_p,
							'N', ie_gerar_titulo_w,	ie_quebra_pagina_w,
							nr_seq_tipo_tema_w);
					
					CALL copia_campo_long_de_para('PLS_PLANO_INSTRUMENTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_plano_instr_w,
								'PLS_CONTRATO_DOC_TEXTO','DS_TEMA','where nr_sequencia = :nr_sequencia','nr_sequencia='||nr_seq_doc_texto_w);
					
					CALL pls_gerar_texto_macro(nr_seq_doc_texto_w,null, nr_seq_contrato_p, nm_usuario_p, 'C');
					end;
				end loop;
				close C02;
			end if;
		end if;
		end;
	end loop;
	close C01;
	end;
end loop;
close C00;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_documento_contrato ( nr_seq_contrato_p bigint, nr_seq_instrumento_p text, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

