SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_email_aud_externo ( nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_aud_grupo_w		bigint;
nr_seq_grupo_w			bigint;
ie_externo_w			varchar(2);
nm_usuario_exec_w		varchar(15);
ds_email_dest_w			varchar(255);
cd_cgc_outorgante_w		varchar(15);
ds_assunto_w			varchar(255);
ds_texto_email_w		varchar(255);
ds_email_remetente_w		varchar(255);

C01 CURSOR FOR
	SELECT	nm_usuario_exec
	from	pls_membro_grupo_aud
	where	nr_seq_grupo	= nr_seq_grupo_w
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	ds_assunto,
		ds_email
	from	pls_analise_aut_email
	where	ie_situacao	= 'A'
	and (coalesce(ie_aplicacao_regra::text, '') = '' or ie_aplicacao_regra = 'E')
	order by nr_sequencia;


BEGIN

nr_seq_aud_grupo_w	:= pls_obter_grupo_analise_atual(nr_seq_auditoria_p);

select	nr_seq_grupo
into STRICT	nr_seq_grupo_w
from	pls_auditoria_grupo
where	nr_sequencia		= nr_seq_aud_grupo_w;

select	CASE WHEN ie_tipo_auditoria=2 THEN 'S'  ELSE 'N' END
into STRICT	ie_externo_w
from	pls_grupo_auditor
where	nr_sequencia	= nr_seq_grupo_w;

if (ie_externo_w	= 'S') then
	ds_email_remetente_w := obter_valor_param_usuario(1270, 13, Obter_Perfil_Ativo, nm_usuario_p, 0);

	open C01;
	loop
	fetch C01 into
		nm_usuario_exec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	ds_email
		into STRICT	ds_email_dest_w
		from	usuario
		where	nm_usuario	= nm_usuario_exec_w;

		open C02;
		loop
		fetch C02 into
			ds_assunto_w,
			ds_texto_email_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			CALL enviar_email(	ds_assunto_w, ds_texto_email_w, ds_email_remetente_w,
					ds_email_dest_w, nm_usuario_p, 'M');

			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
