CREATE OR REPLACE PROCEDURE pls_gerar_entrevista_qualif ( nr_seq_pessoa_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_estabelecimento_w		smallint;
qt_entrevista_w			bigint;


BEGIN

/* Obter dados dados da pessoa da proposta */

select	nr_seq_proposta,
	cd_beneficiario
into STRICT	nr_seq_proposta_w,
	cd_pessoa_fisica_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_pessoa_proposta_p;

/* Obter dados da proposta */

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_w;

select	count(*)
into STRICT	qt_entrevista_w
from	pls_entrevista_qualificada
where	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	coalesce(dt_fim_entrevista::text, '') = '';

if (qt_entrevista_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267013, null); /* A pessoa selecionada j√° possui uma entrevista qualificada em aberto! */
end if;

insert into pls_entrevista_qualificada(
	nr_sequencia, cd_estabelecimento, cd_pessoa_fisica,
	dt_inicio_entrevista, dt_atualizacao, nm_usuario,
	dt_atualizacao_nrec, nm_usuario_nrec, dt_fim_entrevista,
	cd_medico_entrevista, nr_seq_proposta)
values (	nextval('pls_entrevista_qualificada_seq'), cd_estabelecimento_w, cd_pessoa_fisica_w,
	clock_timestamp(), clock_timestamp(), nm_usuario_p,
	clock_timestamp(), nm_usuario_p, null,
	null, nr_seq_proposta_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
