CREATE OR REPLACE PROCEDURE pls_gerar_evento_conta_medica ( nr_seq_evento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
nr_seq_segurado_w		bigint;
nr_seq_prestador_exec_w		bigint;
qt_evento_pessoa_w		bigint;
qt_existe_w			bigint;
dt_mes_conta_medica_w		timestamp;

 
C01 CURSOR FOR 
	SELECT	dt_mes_conta_medica 
	from	pls_evento_conta_medica 
	where	nr_seq_evento	= coalesce(nr_seq_evento_p,0) 
	and	ie_situacao	= 'A' 
	order by	dt_mes_conta_medica;

C02 CURSOR FOR 
	SELECT	distinct 
		b.nr_seq_segurado, 
		b.nr_seq_prestador_exec 
	from	pls_protocolo_conta a, 
		pls_conta b 
	where	a.nr_sequencia = b.nr_seq_protocolo 
	and	a.dt_protocolo = dt_mes_conta_medica_w 
	and	a.ie_tipo_protocolo = 'C';


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	dt_mes_conta_medica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	open C02;
	loop 
	fetch C02 into	 
		nr_seq_segurado_w, 
		nr_seq_prestador_exec_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL pls_inserir_evento_pessoa(nr_seq_segurado_w, nr_seq_prestador_exec_w, nr_seq_evento_p, nm_usuario_p);
		end;
	end loop;
	close C02;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
