LEFT OUTER JOIN ans_grupo_despesa k ON (r.nr_seq_grupo_ans = k.nr_sequencia)
, pls_conta d
LEFT OUTER JOIN pls_plano n ON (d.nr_seq_plano = n.nr_sequencia)
, pls_segurado s
LEFT OUTER JOIN pls_contrato t ON (s.nr_seq_contrato = t.nr_sequencia)
