							where	c.ie_tipo = 'A'
							and 	substr(ctb_obter_classif_conta(c.cd_conta_contabil, c.cd_classificacao, c.dt_inicio_vigencia),1,5) = '2.1.1'))
			and  	vet_titulo_pp.ie_origem_titulo <> '4'
			group by 'O',
