				l.nr_sequencia,
				dt_final_w,
				l.dt_mes_competencia,
				i.cd_conta_credito,
				p.nr_sequencia				
			having  sum(i.vl_item) <> 0
			
union all

			select  'T' ie_tipo_evento,
				p.ie_tipo_relacao,
				vet_titulo_pp.nr_titulo nr_titulo,
				f.nr_sequencia nr_seq_lote,
