				null ie_tipo_contratacao,
				null ie_segmentacao,
				null nr_seq_contrato,
				null dt_ocorrencia,
				null vl_pagar,
				null nr_seq_pagador,
				p.nr_sequencia,
				null ie_tipo_segurado,
				null nr_protocolo_ans,
				'LP' ie_tipo_documento,
				null ie_tipo_grupo_ans,
				null ie_preco,
				null nr_seq_congenere,
				null dt_mes_competencia
			from	pls_pp_base_atual_trib		r,
