			and	b.nr_sequencia		= r.nr_seq_trib_pessoa
			and	f.nr_sequencia		= r.nr_seq_lote
			and	i.nr_sequencia		= r.nr_seq_item_lote
			and	p.nr_sequencia		= b.nr_seq_prestador
			and	p.nr_sequencia		= t.nr_seq_prestador
			and	f.nr_sequencia		= t.nr_seq_lote
			and  	f.dt_mes_competencia 	<= dt_final_w
			and  	obter_empresa_estab(vet_titulo_pp.cd_estabelecimento) = cd_empresa_w
			and	((ie_gerar_ind_classif_w <> 'N')
			or  	r.cd_conta_debito in (	select c.cd_conta_contabil
